INSERT INTO `uz` (`id`, `otazka`, `odpoved_a`, `odpoved_b`, `odpoved_c`, `spravna_odpoved`, `status`) VALUES
(1, 'Přijaté zákony podepisuje:', 'Předseda Poslanecké sněmovny, prezident republiky a předseda vlády.', 'Předseda vlády a prezident republiky.', 'Předseda Senátu, předseda Poslanecké sněmovny a prezident republiky.', 'a', 'Otázka OK, odpověď OK\r'),
(2, 'Vyhlášené mezinárodní smlouvy, k jejichž ratifikaci dal Parlament souhlas a jimiž je Česká republika vázána, jsou součástí právního řádu; stanoví-li mezinárodní smlouva něco jiného než zákon:', 'Použije se národní úprava.', 'Rozhodne o použití svým usnesením vláda.', 'Použije se mezinárodní smlouva.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(3, 'Prezidentem republiky:', 'Nemůže být zvolen občan, kterému je 40 let.', 'Nemůže nikdo být zvolen více než dvakrát.', 'Nemůže nikdo být zvolen více než dvakrát za sebou.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(4, 'Mandát poslance nebo senátora vzniká:', 'Složením slibu do rukou předsedy Senátu.', 'Zvolením.', 'Složením slibu do rukou prezidenta republiky.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(5, 'Zákonodárná moc v České republice náleží:', 'Parlamentu a vládě.', 'Parlamentu.', 'Parlamentu a zastupitelstvům krajů.', 'b', 'Otázka OK, odpověď OK\r'),
(6, 'Vydávat nařízení k provedení zákona a v jeho mezích může podle Ústavy České republiky:', 'Ministerstvo nebo jiný ústřední správní úřad na základě zmocnění v zákoně.', 'Vláda, je-li k tomu zákonem výslovně zmocněna.', 'Vláda i bez výslovného zmocnění v zákoně.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(7, 'Návrh zákona může podle Ústavy České republiky podat:', 'Poslanec,  skupina  poslanců,  Senát,  vláda  nebo  zastupitelstvo  vyššího  územního samosprávného celku.', 'Poslanec, skupina poslanců, senátor, vláda nebo zastupitelstvo vyššího územního samosprávného celku.', 'Poslanecká sněmovna, Senát, vláda nebo zastupitelstvo samosprávného celku.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(8, 'Komory zákonodárného sboru České republiky se nazývají:', 'Parlament a Senát.', 'Vláda, Parlament a Senát.', 'Poslanecká sněmovna a Senát.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(9, 'Mandát poslance nebo senátora nezaniká:', 'Odepřením slibu nebo složením slibu s výhradou.', 'Pravomocným odsouzením k trestu odnětí svobody.', 'Vzdáním se mandátu.', 'b', 'Otázka OK, odpověď OK\r'),
(10, 'Soudce je jmenován do funkce:', 'Prezidentem republiky bez časového omezení.', 'Předsedou Senátu bez časového omezení.', 'Prezidentem republiky na dvacet let.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(11, 'Člen vlády:', 'Má právo účastnit se schůzí obou komor Parlamentu, jejich výborů a komisí.', 'Má právo účastnit se schůzí obou komor Parlamentu; právo účastnit se schůzí jejich výborů a komisí má, jen pokud byl pozván.', 'Nemá právo účastnit se schůzí obou komor Parlamentu, jejich výborů a komisí, není-li členem některé z komor.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(12, 'Česká republika se podle Ústavy České republiky člení na:', 'Obce, které jsou základními územními samosprávnými celky, a okresy, které jsou nižšími samosprávnými celky, a kraje, které jsou vyššími územními samosprávnými celky.', 'Obce, které jsou základními územními samosprávnými celky, města a kraje, které jsou vyššími územními samosprávnými celky.', 'Obce, které jsou základními územními samosprávnými celky, a kraje, které jsou vyššími územními samosprávnými celky.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(13, 'Pravomocí prezidenta republiky není:', 'Jmenovat členy Bankovní rady České národní banky.', 'Jmenovat a povyšovat generály.', 'Jmenovat nejvyššího státního zástupce.', 'c', 'Otázka OK, odpověď OK\r'),
(14, 'K přijetí návrhu na vyslovení nedůvěry vládě je třeba souhlasu:', 'Nadpoloviční většiny všech poslanců.', 'Nadpoloviční většiny přítomných poslanců.', 'Nadpoloviční většiny všech poslanců a nadpoloviční většiny přítomných senátorů.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(15, 'Soudce Ústavního soudu lze zadržet jen:', 'Byl-li dopaden při spáchání trestného činu, anebo bezprostředně poté.', 'Byl-li dopaden při spáchání trestného činu, anebo při důvodném podezření ze spáchání zvlášť závažného zločinu.', 'Při důvodném podezření ze spáchání trestného činu; zadržet ho však nelze na půdě Ústavního soudu.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(16, 'Rozsudek se vyhlašuje:', 'Veřejně s výjimkami stanovenými zákonem.', 'Veřejně s výjimkami v zájmu ochrany dětí a mladistvých.', 'Vždy veřejně.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(17, 'Nejvyšší kontrolní úřad:', 'Vykonává kontrolu hospodaření se státním majetkem a plnění státního rozpočtu.', 'Předkládá Poslanecké sněmovně návrh státního závěrečného účtu.', 'Je orgánem podřízeným vládě.', 'a', 'Otázka OK, odpověď OK\r'),
(18, 'Obec je podle Ústavy České republiky samostatně spravována:', 'Starostou.', 'Zastupitelstvem.', 'Radou obce.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(19, 'Státními symboly České republiky jsou:', 'Velký a malý státní znak, korunovační klenoty, státní vlajka, státní pečeť a státní hymna.', 'Velký a malý státní znak, státní barvy, státní vlajka, státní pečeť, korunovační klenoty a státní hymna.', 'Velký a malý státní znak, státní barvy, státní vlajka, vlajka prezidenta republiky, státní pečeť a státní hymna.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(20, 'Do Poslanecké sněmovny může být podle Ústavy zvolen každý:', 'Občan Evropské unie, který má právo volit a dosáhl věku 18 let.', 'Občan České republiky, který je občanem České republiky déle než 10 let a dosáhl věku 18 let.', 'Občan České republiky, který má právo volit a dosáhl věku 21 let.', 'c', 'Otázka opravena OK, odpověď OK\r'),
(21, 'Volební zákon musí být přijat:', 'Ústavní většinou (? všech poslanců i senátorů) v obou komorách Parlamentu.', 'Absolutní většinou (všech poslanců) v Poslanecké sněmovně.', 'Prostou většinou (přítomných poslanců či senátorů) v obou komorách Parlamentu.', 'c', 'Otázka OK, odpověď OK\r'),
(22, 'Vláda je podle Ústavy:', 'Vrcholným orgánem státní moci.', 'Vrcholným orgánem výkonné moci.', 'Vrcholným orgánem zákonodárné moci.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(23, 'Hlavním cílem činnosti České národní banky je dle Ústavy České republiky:', 'Péče o peněžní oběh.', 'Péče o hospodářský cyklus.', 'Péče o cenovou stabilitu.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(24, 'Do působnosti Ústavního soudu nepatří:', 'Rozhodování o ústavní žalobě Senátu proti prezidentu republiky.', 'Rozhodování o zrušení jiných právních předpisů než jsou zákony, jsou-li v rozporu se zákonem.', 'Udělování souhlasu s trestním stíháním poslance nebo senátora.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(25, 'Slib prezidenta republiky zní:', '„Slibuji věrnost České republice. Slibuji, že budu zachovávat její Ústavu a zákony. Slibuji na svou čest, že svůj úřad budu zastávat v zájmu všeho lidu a podle svého nejlepšího vědomí a svědomí.“', '„Slibuji věrnost České republice. Slibuji na svou čest, že svůj úřad budu zastávat v zájmu všech občanů a budu usilovat o spravedlivé zákony a důstojnou reprezentaci státu v zahraničí.“', '„Slibuji věrnost Ústavě České republiky a jejím zákonům. Slibuji na svou čest, že svůj úřad budu vykonávat poctivě, důstojně a ve prospěch všeho lidu.“', 'a', 'Otázka OK, odpověď OK\r'),
(26, 'Součástí ústavního pořádku není:', 'Lisabonská smlouva.', 'Listina základních práv a svobod.', 'Ústavní zákon č. 110/1998 Sb., o bezpečnosti České republiky.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(27, 'Ústavní soud se skládá z:', '15 soudců, kteří jsou jmenováni na dobu deseti let.', '10 soudců, kteří jsou jmenováni na dobu patnácti let.', '15 soudců, kteří jsou jmenováni na dobu neomezenou.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(28, 'Povinnost poskytovat informace podle zákona o svobodném přístupu k informacím se:', 'Netýká dotazů na názory, budoucí rozhodnutí a vytváření nových informací.', 'Nevztahuje na informace vztahující se k působnosti povinného subjektu.', 'Vztahuje též na informace, které jsou předmětem průmyslového vlastnictví.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(29, 'Žadatelem se pro účely zákona o svobodném přístupu k informacím rozumí:', 'Pouze každá fyzická osoba, která žádá o informaci.', 'Pouze každá právnická osoba, která žádá o informaci.', 'Každá fyzická i právnická osoba, která žádá o informaci.', 'c', 'Otázka OK, odpověď OK\r'),
(30, 'Informace poskytnuté na žádost povinný subjekt zveřejní způsobem umožňujícím dálkový přístup ve lhůtě:', 'Do 30 dnů od poskytnutí informací.', 'Do 15 dnů od poskytnutí informací.', 'Do 7 dnů od poskytnutí informací.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(31, 'Povinné subjekty podle zákona o svobodném přístupu k informacím:', 'Musí do 1. března zaslat údaje o počtu vyřízených a obdržených žádostí o poskytnutí informace za minulý kalendářní rok na Ministerstvo vnitra.', 'Musí vždy do 1. března zveřejnit výroční zprávu za předcházející kalendářní rok o své činnosti v oblasti poskytování informací.', 'Musí do 1. března zveřejnit všechny poskytnuté informace za minulý kalendářní rok v Národním katalogu otevřených dat.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(32, 'O odvolání proti rozhodnutí povinného subjektu o odmítnutí žádosti o poskytnutí informace rozhoduje podle zákona o svobodném přístupu k informacím:', 'Úřad pro ochranu osobních údajů.', 'Správní soud.', 'Nadřízený orgán.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(33, 'Podle zákona o svobodném přístupu k informacím jsou povinny poskytovat informace povinné subjekty, jimiž jsou:', 'Jen státní orgány.', 'Státní orgány, územní samosprávné celky a jejich orgány a veřejné instituce.', 'Jen státní orgány, obce a kraje.', 'b', 'Otázka OK, odpověď OK\r'),
(34, 'Službou informačního systému veřejné správy je:', 'Veřejné publikování informací o činnosti orgánů veřejné správy.', 'Poskytování informační podpory občanům pro veřejnou kontrolu činnosti orgánů veřejné moci.', 'Činnost informačního systému veřejné správy uspokojující požadavky oprávněné osoby.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(35, 'Portál veřejné správy je:', 'Web provozovaný Úřadem vlády, sloužící občanům pro autorizovaný přístup ke službám informačních systémů veřejné správy.', 'Informační systém veřejné správy zajišťující přístup k informacím veřejných orgánů a komunikaci s veřejnými orgány.', 'Web umožňující občanům elektronický styk s úřady veřejné správy, které jsou v dané věci věcně a místně příslušné.', 'b', 'Otázka opravena OK, odpověď OK\r'),
(36, 'Povinnost zajistit si atestaci dlouhodobého řízení informačních systémů veřejné správy se nevztahuje na:', 'Centrální orgány státní správy.', 'Obce, které vykonávají přenesenou působnost pouze v základním rozsahu.', 'Orgány veřejné správy, které mají vytvořenu a vydánu informační koncepci orgánu veřejné správy, uplatňují ji v praxi a vyhodnocují její dodržování.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(37, 'Procesní pravidla výkonu veřejnosprávní kontroly, která nejsou upravena zákonem o finanční kontrole, se podpůrně řídí:', 'Rozpočtovými pravidly.', 'Kontrolním řádem.', 'Zákonem o auditorech.', 'b', 'Otázka OK, odpověď OK\r'),
(38, 'Audit výkonu podle zákona o finanční kontrole je zaměřen na zkoumání:', 'Hospodárnosti, efektivnosti a účelnosti operací.', 'Systémů zajištění příjmů a financování organizace.', 'Údajů vykázaných ve finančních výkazech.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(39, 'Za závažné zjištění z vykonaných finančních kontrol se podle zákona o finanční kontrole považuje:', 'Zjištění, na jehož základě kontrolní orgán oznámil podle zvláštního právního předpisu státnímu zástupci nebo policejním orgánům skutečnosti nasvědčující tomu, že byl spáchán trestný čin.', 'Zjištění neoprávněného použití, zadržení, ztráty nebo poškození veřejných prostředků v hodnotě přesahující 50 000 Kč.', 'Zjištění, na jehož základě kontrolní orgán zahájil řízení o přestupku.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(40, 'Ke změně názvu obce dává souhlas:', 'Soud na základě žádosti podepsané nadpoloviční většinou všech členů zastupitelstva obce.', 'Krajský úřad na návrh starosty obce.', 'Ministerstvo vnitra na návrh obce.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(41, 'Zřízení finančního a kontrolního výboru dle zákona o obcích je:', 'Obligatorní.', 'Obligatorní pouze v případě obcí s pověřeným obecním úřadem.', 'Fakultativní.', 'a', 'Otázka OK, odpověď OK\r'),
(42, 'Mezi vyhrazené pravomoci zastupitelstva obce dle zákona o obcích nepatří:', 'Rozhodování o nabytí a převodu hmotných nemovitých věcí včetně vydání nemovitostí podle zvláštních zákonů, s výjimkou inženýrských sítí a pozemních komunikací; převodu bytů a nebytových prostorů z majetku obce.', 'Zřizování a rušení příspěvkových organizací a organizačních složek obce, schvalování jejich zřizovací listiny.', 'Plnění úkolů statutárního orgánu zaměstnavatele podle zvláštních právních předpisů vůči zaměstnancům obce zařazeným do obecního úřadu.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(43, 'Vydávání obecně závazných vyhlášek kraje je:', 'Pravomocí krajského úřadu.', 'Vyhrazenou pravomocí zastupitelstva kraje.', 'Nevyhrazenou pravomocí rady kraje.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(44, 'Má-li hejtman kraje za to, že usnesení rady kraje je nesprávné:', 'Pozastaví jeho výkon a věc předloží k rozhodnutí nejbližšímu zasedání zastupitelstva kraje.', 'Zruší jej a o věci sám rozhodne.', 'Postoupí věc k rozhodnutí Ministerstvu vnitra.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(45, 'Zasedání zastupitelstva kraje:', 'Písemně svolává a řídí zpravidla hejtman kraje.', 'Oznamuje Ministerstvo vnitra prostřednictvím svých internetových stránek.', 'Je vždy neveřejné.', 'a', 'Otázka OK, odpověď OK\r'),
(46, 'Mandát člena zastupitelstva hlavního města Prahy vzniká:', 'Zvolením; ke zvolení dojde ukončením hlasování.', 'Složením slibu na začátku prvního zasedání zastupitelstva hlavního města Prahy, jehož se po svém zvolení zúčastní.', 'Účastí na ustavujícím zasedání zastupitelstva hlavního města Prahy.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(47, 'Dle zákona o hlavním městě Praze je k platnému usnesení, rozhodnutí nebo volbě třeba souhlasu:', 'Nadpoloviční většiny přítomných členů zastupitelstva hlavního města Prahy, nestanoví-li zvláštní právní předpis jinak.', 'Nadpoloviční většiny všech členů zastupitelstva hlavního města Prahy, nestanoví-li zvláštní právní předpis jinak.', 'Všech přítomných členů zastupitelstva hlavního města Prahy, nestanoví-li zvláštní právní předpis jinak.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(48, 'Městské části v rozsahu stanoveném zákonem o hlavním městě Praze a Statutem hlavního města Prahy vystupují v právních vztazích:', 'Jménem hlavního města Prahy, které nese odpovědnost z těchto vztahů vyplývající.', 'Jménem orgánu městské části v závislosti na předmětu právního vztahu.', 'Svým jménem a nesou odpovědnost z těchto vztahů vyplývající.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(49, 'Za předpokladu, že si plénum toto rozhodování nevyhradí, nerozhoduje v plénu Ústavní soud České republiky:', 'Pokud senát nerozhodl, protože žádný z návrhů na usnesení nezískal většinu hlasů.', 'O ústavní stížnosti orgánů územní samosprávy proti nezákonnému zásahu státu.', 'O zrušení zákonů nebo jejich jednotlivých ustanovení.', 'b', 'Otázka OK, odpověď OK\r'),
(50, 'Ve Sbírce zákonů České republiky se vyhlašují nálezy, kterými Ústavní soud rozhodl:', 'O návrhu na zrušení zákona nebo jiného právního předpisu anebo jejich jednotlivých ustanovení.', 'O opravném prostředku proti rozhodnutí ve věci ověření volby poslance nebo senátora.', 'O tom, zda rozhodnutí o rozpuštění politické strany nebo jiné rozhodnutí týkající se činnosti politické strany je ve shodě s ústavními nebo jinými zákony.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(51, 'Předseda Ústavního soudu:', 'Může se souhlasem pléna pověřit místopředsedy Ústavního soudu trvalým plněním některých svých úkolů.', 'Může v rozsahu nezbytném pro chod Ústavního soudu a po projednání v Senátu pověřit místopředsedu Ústavního soudu trvalým plněním úkolů stanovených zákonem.', 'Nemůže s výjimkou své nepřítomnosti pověřovat místopředsedy Ústavního soudu trvalým plněním některých úkolů souvisejících s výkonem veřejné funkce.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(52, 'Návrh na zrušení jiného právního předpisu nebo jeho jednotlivých ustanovení není oprávněn podat:', 'Prezident republiky.', 'Senát Ústavního soudu v souvislosti s rozhodováním o ústavní stížnosti.', 'Veřejný ochránce práv.', 'c', 'Otázka OK, odpověď opravena OK (samo, bylo původně a)\r'),
(53, 'Při projednání návrhů se Ústavní soud:', 'Musí vždy řídit pořadím, v němž mu návrhy došly.', 'Nemusí řídit pořadím, v němž mu návrhy došly, jestliže se usnese, že věc, které se některý návrh týká, je naléhavá.', 'Nemusí řídit pořadím, v němž mu návrhy došly, pokud přednostním projednáním svůj návrh podmíní Poslanecká sněmovna nebo Senát Parlamentu; o upřednostnění návrhu sepíší zápis.', 'b', 'Otázka OK, odpověď OK\r'),
(54, 'Pro rozhodování věcí, které nepatří do působnosti pléna, vytváří Ústavní soud:', 'Čtyři tříčlenné senáty.', 'Dva tříčlenné senáty.', 'Jeden šestičlenný senát.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(55, 'Návrh na posouzení souladu mezinárodní smlouvy s ústavním zákonem nemůže podat:', 'Skupina nejméně 41 poslanců nebo skupina nejméně 17 senátorů od okamžiku, kdy byl Parlamentem k ratifikaci mezinárodní smlouvy vysloven souhlas, do okamžiku, kdy prezident republiky smlouvu ratifikoval.', 'Komora Parlamentu od okamžiku, kdy jí byla mezinárodní smlouva předložena k vyslovení souhlasu k ratifikaci, do okamžiku, kdy se na vyslovení souhlasu usnesla.', 'Prezident republiky předtím, než mu byla mezinárodní smlouva předložena k ratifikaci.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(56, 'Zákon o stanovení obcí s pověřeným obecním úřadem:', 'Upravuje postavení hl. m. Prahy v rozsahu nezbytném pro výkon samostatné působnosti.', 'Upravuje postavení hl. m. Prahy jako obce s pověřeným obecním úřadem pro Černošice a Brandýs nad Labem – Stará Boleslav.', 'Neupravuje postavení hl. m. Prahy.', 'c', 'Otázka opravena OK, odpověď OK (samo)\r'),
(57, 'Obce s pověřeným obecním úřadem jsou stanoveny:', 'V příloze zákona o stanovení obcí s pověřeným obecním úřadem.', 'Vyhláškou ministerstva.', 'Nařízením vlády.', 'a', 'Otázka opravena OK, odpověď OK\r'),
(58, 'V zákoně o územně správním členění státu:', 'Okresy nejsou vymezeny prostřednictvím správních obvodů obcí s rozšířenou působností.', 'Správní obvod kraje s výjimkou správního obvodu hlavního města Prahy se člení na správní obvody obcí s rozšířenou působností.', 'Nejsou obce s rozšířenou působností obsaženy.', 'b', 'Otázka opravena OK, odpověď opravena OK (samo, bylo c)\r'),
(59, 'Správní obvod obce s rozšířenou působností je podle zákona o územně správním členění státu vymezen:', 'Výčtem území obcí.', 'Výčtem území obcí a vojenských újezdů.', 'Výčtem území okresů.', 'b', 'Otázka opravena OK, odpověď OK (samo)\r'),
(60, 'Území vojenského újezdu je vymezeno:', 'Pomocí obcí.', 'Pomocí částí obcí.', 'Pomocí katastrálních území.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(61, 'Která z těchto územních jednotek není podle zákona o územně správním členění státu vytvořena?', 'Okres Praha-město.', 'Okres Brno-město.', 'Okres Plzeň-město.', 'a', 'Otázka opravena OK, odpověď OK\r'),
(62, 'Zákon o územně správním členění státu:', 'Ve správním obvodu hl. m. Prahy vymezuje 10 okresů.', 'Ve správním obvodu hl. m. Prahy vymezuje 10 obvodů na úrovni okresů.', 'Neupravuje územní členění hl. m. Prahy.', 'b', 'Otázka opravena OK, odpověď opravena OK (samo, bylo c)\r'),
(63, 'Podle zákona o zřízení ministerstev a jiných ústředních orgánů státní správy platí, že:', 'V čele každého ústředního orgánu státní správy stojí člen vlády.', 'V čele každého ministerstva stojí člen vlády.', 'Každý člen vlády řídí některé ministerstvo.', 'b', 'Otázka opravena OK, odpověď OK\r'),
(64, 'Ústředním orgánem státní správy není:', 'Česká správa sociálního zabezpečení.', 'Český báňský úřad.', 'Český telekomunikační úřad.', 'a', 'Otázka opravena OK, odpověď OK\r'),
(65, 'Úřad vlády České republiky:', 'Je nadřízen všem ministerstvům České republiky.', 'Rozhoduje o odvolání proti rozhodnutím vlády České republiky.', 'Plní úkoly spojené s odborným, organizačním a technickým zabezpečením činnosti vlády České republiky.', 'c', 'Otázka OK, odpověď OK\r'),
(66, 'Ústředním orgánem státní správy pro ochranu oznamovatelů je:', 'Ministerstvo školství, mládeže a tělovýchovy.', 'Ministerstvo vnitra.', 'Ministerstvo spravedlnosti.', 'c', 'Otázka opravena OK, odpověď OK\r'),
(67, 'Ústředním orgánem státní správy není:', 'Digitální a informační agentura.', 'Nejvyšší správní soud.', 'Národní bezpečnostní úřad.', 'b', 'Otázka opravena OK, odpověď OK\r'),
(68, 'Ústředním orgánem státní správy pro vězeňství je:', 'Ministerstvo vnitra.', 'Ministerstvo spravedlnosti.', 'Ministerstvo práce a sociálních věcí.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(69, 'Ústředním orgánem státní správy není:', 'Úřad pro ochranu osobních údajů.', 'Správa státních hmotných rezerv.', 'Státní veterinární správa České republiky.', 'c', 'Otázka OK, odpověď OK\r'),
(70, 'Nesprávné je tvrzení, že podle zákona o zřízení ministerstev a jiných ústředních orgánů státní správy:', 'Ministerstvo financí řídí Českou národní banku.', 'Ministerstvo financí je ústředním orgánem státní správy pro zavedení jednotné měny euro na území České republiky.', 'Ministerstvo financí spolu s Českou národní bankou připravuje a předkládá vládě návrhy zákonných úprav v oblasti měny.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(71, 'Česká obchodní inspekce je podřízena:', 'Ministerstvu zemědělství.', 'Ministerstvu průmyslu a obchodu.', 'Ministerstvu zdravotnictví.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(72, 'Ústředním orgánem státní správy není:', 'Veřejný ochránce práv.', 'Úřad průmyslového vlastnictví.', 'Národní sportovní agentura.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(73, 'Nesprávné je tvrzení, že podle zákona o zřízení ministerstev a jiných ústředních orgánů státní správy:', 'Ministr zahraničních věcí ratifikuje mezinárodní smlouvy.', 'Ministerstvo zahraničních věcí plní úkoly při správě majetku České republiky v zahraničí.', 'Ministerstvo zahraničních věcí uděluje souhlas v případě dovozu a vývozu vojenského materiálu.', 'a', 'Otázka OK, odpověď OK\r'),
(74, 'Nesprávné je tvrzení, že podle zákona o zřízení ministerstev a jiných ústředních orgánů státní správy:', 'Ministerstvo pro místní rozvoj je ústředním orgánem státní správy ve věcech politiky bydlení.', 'Ministerstvo pro místní rozvoj je ústředním orgánem státní správy ve věcech pohřebnictví.', 'Ministerstvo pro místní rozvoj je ústředním orgánem státní správy pro řízení činnosti obcí a měst.', 'c', 'Otázka opravena OK, odpověď OK (samo)\r'),
(75, 'Koordinační úloha pro oblast státní služby je podle zákona o zřízení ministerstev a jiných ústředních orgánů státní správy svěřena:', 'Úřadu vlády České republiky.', 'Ministerstvu práce a sociálních věcí.', 'Ministerstvu vnitra.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(76, 'Nesprávné je tvrzení, že podle zákona o zřízení ministerstev a jiných ústředních orgánů státní správy:', 'Ministerstvu průmyslu a obchodu je podřízen Úřad pro ochranu hospodářské soutěže a Hospodářská komora České republiky.', 'Ministerstvu průmyslu a obchodu je podřízena Česká obchodní inspekce.', 'Ministerstvo průmyslu a obchodu je ústředním orgánem státní správy pro technickou normalizaci, metrologii a státní zkušebnictví.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(77, 'Nesprávné je tvrzení, že podle zákona o zřízení ministerstev a jiných ústředních orgánů státní správy:', 'Ministerstvo obrany povolává občany České republiky k plnění branné povinnosti.', 'Ministerstvo obrany vyhlašuje jménem České republiky válečný stav.', 'Ministerstvo obrany je ústředním orgánem státní správy pro řízení Armády České republiky.', 'b', 'Otázka OK, odpověď OK\r'),
(78, 'Úkoly spojené s odborným, organizačním a technickým zabezpečením činnosti členů vlády, kteří nejsou pověřeni řízením ministerstva nebo jiného úřadu, plní:', 'Ministerstvo vnitra.', 'Úřad pro zastupování státu ve věcech majetkových.', 'Úřad vlády České republiky.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(79, 'Mezi správní úřady podřízené Ministerstvu zemědělství nepatří:', 'Český hydrometeorologický ústav.', 'Státní veterinární správa České republiky.', 'Státní zemědělská a potravinářská inspekce.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(80, 'Nesprávné je tvrzení, že podle zákona o zřízení ministerstev a jiných ústředních orgánů státní správy:', 'Ministerstvo spravedlnosti řídí, kontroluje a sjednocuje rozhodování soudů.', 'Ministerstvo spravedlnosti zastupuje Českou republiku při vyřizování stížností na porušení Úmluvy o ochraně lidských práv a základních svobod.', 'Ministerstvu spravedlnosti je podřízena Vězeňská služba České republiky.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(81, 'Nesprávné je tvrzení, že Ministerstvo školství, mládeže a tělovýchovy je podle zákona o zřízení ministerstev a jiných ústředních orgánů státní správy ústředním orgánem státní správy pro:', 'Předškolní zařízení, školská zařízení, základní školy, střední školy a vysoké školy.', 'Péči o rodinu a děti.', 'Státní péči o děti, mládež a tělesnou výchovu.', 'b', 'Otázka OK, odpověď OK\r'),
(82, '82. Do působnosti Ministerstva obrany podle zákona o zřízení ministerstev a jiných ústředních orgánů státní správy nespadá:', 'Správa vojenských újezdů.', 'Udělování souhlasu v případech dovozu a vývozu vojenského materiálu.', 'Koordinace činnosti ústředních orgánů, správních orgánů a orgánů samosprávy a právnických osob důležitých pro obranu státu při přípravě k obraně.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(83, 'Ústředním orgánem státní správy je:', 'Česká geologická služba.', 'Úřad pro civilní letectví České republiky.', 'Český statistický úřad.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(84, 'Nesprávné je tvrzení, že podle zákona o zřízení ministerstev a jiných ústředních orgánů státní správy vláda České republiky:', 'Řídí, kontroluje a sjednocuje činnost ministerstev.', 'Zřizuje ministerstva svým usnesením.', 'Může uložit ministerstvu, aby připravilo návrh právního předpisu týkajícího se věci, která jinak nepatří do jeho působnosti.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(85, 'V čele Legislativní rady vlády stojí:', 'Vedoucí Úřadu vlády České republiky.', 'Člen vlády.', 'Člen Legislativní rady vlády zvolený na jejím jednání z řad jejích členů.', 'b', 'Otázka OK, odpověď OK\r'),
(86, 'Podle ustanovení Listiny základních práv a svobod:', 'Je právo bránit život svůj či život nezletilého dítěte i se zbraní zaručeno za podmínek, které stanoví podzákonný předpis.', 'Je právo bránit život svůj či život jiného člověka i se zbraní zaručeno za podmínek, které stanoví zákon.', 'Bránit život svůj či život jiného člověka se zbraní není možné.', 'b', 'Otázka opravena OK, odpověď opravena OK (samo, bylo c)\r'),
(87, 'Státní moc lze podle ustanovení Listiny základních práv a svobod:', 'Uplatňovat pouze v případech a mezích stanovených zákonem, a to způsobem, který stanoví zákon.', 'Uplatňovat v případech stanovených zákony, podzákonnými právními předpisy a tradicí založenými principy.', 'Uplatňovat s přihlédnutím k okolnostem konkrétního případu a dle uvážení orgánu státní správy i mimo zákonný rámec.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(88, 'Podle Listiny základních práv a svobod jen zákon stanoví,:', 'Které jednání je trestným činem; jaký trest za jeho spáchání lze uložit lze stanovit i jiným právním předpisem.', 'Které jednání je trestným činem; zákon stanoví také, jaký trest lze za jeho spáchání uložit.', 'Které jednání je zvlášť závažným zločinem; zákon stanoví také, jaký trest lze za jeho spáchání uložit.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(89, 'Zatčená osoba musí být podle Listiny základních práv a svobod odevzdána soudu do:', '12 hodin.', '24 hodin.', '48 hodin.', 'b', 'Otázka OK, odpověď OK\r'),
(90, 'Porušení listovního tajemství dle Listiny základních práv a svobod:', 'Je možné, pokud tak stanoví zákon.', 'Není možné.', 'Není bez usnesení Nejvyššího správního soudu možné, to neplatí, jde-li o zprávu zasílanou poštou.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(91, 'Podle ustanovení Listiny základních práv a svobod:', 'Je poskytována větší ochrana dětem narozeným mimo manželství.', 'Mají děti narozené v manželství i mimo ně stejná práva.', 'Je větší ochrana poskytována dětem narozeným v manželství.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(92, 'Listina základních práv a svobod upravuje v čl. 15 otázky náboženského vyznání a víry. Vyberte z níže uvedených možností tu, která nejlépe odpovídá smyslu čl. 15 ve vztahu k náboženskému vyznání a víře:', 'Svoboda myšlení, svědomí a náboženského vyznání je zaručena. Každý má právo být bez náboženského vyznání.', 'Svoboda myšlení, svědomí a náboženského vyznání je zaručena. Každý má právo změnit své náboženství nebo víru, pokud to náboženství nebo víra, ke které se hlásí, připouští.', 'Svoboda myšlení, svědomí a náboženského vyznání je zaručena. Každý má právo změnit své náboženství nebo víru anebo být bez náboženského vyznání.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(93, 'Občané mají podle Listiny základních práv a svobod právo na:', 'Bezplatné vzdělání v základních a středních školách, ne však na vysokých školách.', 'Bezplatné vzdělání v základních a středních školách, podle schopností občana a možností společnosti též na vysokých školách.', 'Bezplatné vzdělání v základních, středních a vysokých školách.', 'b', 'Otázka OK, odpověď OK\r'),
(94, 'Podle Listiny základních práv a svobod mohou být práva rodičů omezena a nezletilé děti od rodičů odloučeny proti jejich vůli:', 'Jen rozhodnutím soudu na základě zákona.', 'Pravomocným rozhodnutím orgánu sociálně-právní ochrany dětí.', 'Rozhodnutím policejního orgánu, je-li to nezbytné k ochraně dětí před domácím násilím.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(95, 'Školní docházka je podle Listiny základních práv a svobod povinná:', 'Do dosažení věku 15 let.', 'Po dobu, kterou stanoví zákon.', 'Jen pro občany České republiky.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(96, 'Právo na zvýšenou ochranu zdraví při práci a na zvláštní pracovní podmínky mají podle Listiny základních práv a svobod:', 'Osoby zdravotně postižené a těhotné ženy.', 'Ženy, mladiství a osoby zdravotně postižené.', 'Děti a osoby zdravotně postižené.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(97, 'Svoboda myšlení dle Listiny základních práv a svobod:', 'Náleží mezi základní lidská práva a svobody.', 'Náleží mezi politická práva.', 'Náleží mezi hospodářská, sociální a kulturní práva.', 'a', 'Otázka OK, odpověď OK\r'),
(98, 'O odborových organizacích podle Listiny základních práv a svobod platí, že:', 'Vznikají nezávisle na státu.', 'Jsou správním orgánem zřízeným na základě zákona k ochraně hospodářských a sociálních zájmů zaměstnanců.', 'Jejich činnost nelze jakkoliv omezit.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(99, 'Listina základních práv a svobod upravuje v čl. 3 rozhodování o národnosti. Při tom stanoví, že:', 'Občan má právo svobodně rozhodovat o své národnosti.', 'Každý rodič má právo svobodně rozhodovat o národnosti svého dítěte.', 'Každý má právo svobodně rozhodovat o své národnosti.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(100, 'Právo na stávku podle Listiny základních práv a svobod nepřísluší:', 'Státním zaměstnancům zařazeným v ústředních orgánech státní správy.', 'Příslušníkům bezpečnostních sborů.', 'Zaměstnancům v dopravě.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(101, 'Mezi politická práva Listina základních práv a svobod nezařazuje:', 'Petiční právo.', 'Právo na svobodu projevu.', 'Právo na život.', 'c', 'Otázka OK, odpověď OK\r'),
(102, 'Podle Listiny základních práv a svobod je v čl. 12 domovní prohlídka:', 'Přípustná jen pro účely trestního řízení, a to na písemný odůvodněný příkaz soudce.', 'Nepřípustná.', 'Přípustná jen pro účely trestního nebo jiného řízení, a to na příkaz státního zástupce.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(103, 'Přezkoumávání rozhodnutí týkajících se základních práv a svobod podle Listiny základních práv a svobod:', 'Nesmí být vyloučeno z pravomoci soudu.', 'Může být vyloučeno z pravomoci soudu.', 'Nesmí být vyloučeno z pravomoci soudu, pokud zákon nestanoví jinak.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(104, 'Listina základních práv a svobod v čl. 30 stanoví, že každý, kdo je v hmotné nouzi, má právo na takovou pomoc, která:', 'Je nezbytná pro zajištění základních životních podmínek.', 'Je nezbytná pro zajištění základních životních podmínek, pokud si stav hmotné nouze nezpůsobil sám svým protiprávním nebo neodpovědným jednáním.', 'Zajišťuje přiměřené hmotné zabezpečení vzhledem k jeho potřebám.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(105, 'Právo na právní pomoc v řízení před soudy, jinými státními orgány či orgány veřejné správy podle Listiny základních práv a svobod:', 'Náleží pouze státnímu občanu České republiky a osobě bez státní příslušnosti s trvalým pobytem na území České republiky.', 'Má každý, za podmínky, že se na něj nevztahuje právo odepřít výpověď.', 'Má každý.', 'c', 'Otázka OK, odpověď OK\r'),
(106, 'Vyvlastnění nebo nucené omezení vlastnického práva je podle Listiny základních práv a svobod možné:', 'Ve veřejném zájmu, a to na základě zákona a za náhradu.', 'Ve veřejném zájmu, a to na základě právního předpisu a za přiměřenou náhradu.', 'Ve veřejném zájmu i bez náhrady, a to na základě zákona.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(107, 'Právo občanů postavit se na odpor je Listinou základních práv a svobod formulováno jako:', 'Právo postavit se na odpor proti každému, kdo by odstraňoval politický systém České republiky snahou ovládnout ústavní orgány, a účinné použití zákonných prostředků je v důsledku znemožněno.', 'Právo postavit se na odpor proti každému, kdo by odstraňoval demokratický řád lidských práv a základních svobod, založený Listinou, jestliže činnost ústavních orgánů a účinné použití zákonných prostředků jsou znemožněny.', 'Právo postavit se na odpor proti každému, kdo by se pokusil ovládnout české ústavní orgány za pomocí vojenské síly a odstraňoval zároveň demokratický řád lidských práv a základních svobod, založený Listinou.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(108, 'Listina základních práv a svobod jako právo občanů příslušejících k národnostním a etnickým menšinám výslovně nezmiňuje:', 'Právo užívat jejich jazyka v úředním styku.', 'Právo na vzdělání v jejich jazyku.', 'Právo na zastoupení v zákonodárných orgánech.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(109, 'Právo na ochranu zdraví je podle Listiny základních práv a svobod řazeno mezi:', 'Základní lidská práva a svobody.', 'Politická práva.', 'Hospodářská, sociální a kulturní práva.', 'c', 'Otázka OK, odpověď OK\r'),
(110, 'Listina základních práv a svobod ve vztahu k cenzuře:', 'Stanovuje, že cenzura je možná jen na základě rozhodnutí soudu.', 'Stanovuje, že cenzura je nepřípustná.', 'Otázku cenzury explicitně neřeší.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(111, 'Žádoucí standardy chování státních zaměstnanců upravuje služební předpis nejvyššího státního tajemníka:', 'O pravidlech etikety.', 'O pravidlech etiky.', 'O pravidlech správního trestání.', 'b', 'Otázka opravena OK, odpověď OK\r'),
(112, 'Mezi oblasti, které upravují pravidla etiky státních zaměstnanců, nepatří:', 'Způsob rozvržení služební doby a přestávek ve službě.', 'Žádoucí jednání a nestrannost státního zaměstnance při výkonu služby.', 'Hospodárnost při využívání prostředků svěřených státnímu zaměstnanci v souvislosti s výkonem služby.', 'a', 'Otázka opravena OK, odpověď OK\r'),
(113, 'Je státní zaměstnanec oprávněn odmítnout jednat s osobou se zdravotním postižením?', 'Ano. Předpokladem však je předchozí souhlas bezprostředně nadřízeného představeného.', 'Ne. Státní zaměstnanec je povinen uplatňovat rovný přístup ke všem osobám.', 'Ano. Státní zaměstnanec je však povinen vysvětlit dotčené osobě důvody, pro které s ní odmítá jednat.', 'b', 'Otázka opravena OK, odpověď OK\r'),
(114, 'Pravidla etiky státních zaměstnanců upravují vstřícnost v jednání státních zaměstnanců:', 'Pouze ve vztahu ke služebnímu orgánu a ostatním zaměstnancům služebního úřadu.', 'Pouze ve vztahu k veřejnosti a představeným.', 'Ve vztahu k veřejnosti a k ostatním zaměstnancům ve služebních úřadech.', 'c', 'Otázka opravena OK, odpověď OK\r'),
(115, 'Státní zaměstnanec si před začátkem zimního období v automobilovém servisu objednal výměnu pneumatik. Při objednání požadoval, aby byla výměna pneumatik provedena přednostně, což zdůvodnil tím, že je státním zaměstnancem a předmětné vozidlo potřebuje pro přepravu do služebního úřadu. Je takové jednání v souladu s pravidly etiky státních zaměstnanců?', 'Nikoliv. Uvedené jednání lze považovat za zastrašování automechanika a mohlo by být podnětem pro orgány činné v trestním řízení k zahájení trestního stíhání.', 'Ano. Státní zaměstnanec v daném případě nenabídl dotčenému automechanikovi přednostní zacházení při jednání se služebním úřadem, v němž je státní zaměstnanec zařazen.', 'Nikoliv. Státní zaměstnanec nesmí využít své služební postavení ve svůj prospěch ve věcech, které nesouvisejí s výkonem služby.', 'c', 'Otázka opravena OK, odpověď OK\r'),
(116, 'Pravidla etiky státních zaměstnanců mají formu:', 'Nařízení vlády.', 'Služebního předpisu nejvyššího státního tajemníka.', 'Metodického pokynu nejvyššího státního tajemníka.', 'b', 'Otázka opravena OK, odpověď OK\r'),
(117, 'Státní zaměstnanec zařazený k výkonu služby v Národní sportovní agentuře přiděluje dotace sportovním klubům. Jeho dvě nezletilé děti jsou členy sportovního klubu v místě bydliště státního zaměstnance a trénují zde tenis. V rámci dotační výzvy, v níž bude státní zaměstnanec o dotaci rozhodovat, zaznamenal, že sportovní klub, v němž jeho děti pravidelně trénují, podal žádost o dotaci. Je v souladu s pravidly etiky státního zaměstnance, aby státní zaměstnanec rozhodoval o přidělení, resp. zamítnutí dotace v této dotační výzvě?', 'Ne, v daném případě by bylo možné jednání státního zaměstnance vyhodnotit jako střet zájmu veřejného s jeho zájmem soukromým.', 'Ne, pokud by měl státní zaměstnanec z přidělení dotace finanční prospěch.', 'Ano, členy sportovního klubu jsou jeho děti, nikoli státní zaměstnanec, který by tak mohl mít z přidělení dotace pouze nefinanční prospěch.', 'a', 'Otázka opravena OK, odpověď OK\r'),
(118, 'V souvislosti s výkonem služby státní zaměstnanec v souladu s pravidly etiky státních zaměstnanců:', 'Nepřijímá pro sebe či jiné osoby žádné výhody.', 'Nepřijímá žádné výhody pro sebe, ale může přijmout výhodu pro svého představeného.', 'Nepřijímá odměny k pracovnímu jubileu poskytované služebním úřadem.', 'a', 'Otázka opravena OK, odpověď OK\r'),
(119, 'Pravidla etiky státních zaměstnanců byla vydána za účelem:', 'Zachování nestrannosti a ochrany veřejného zájmu.', 'Zachování odbornosti a klientelismu.', 'Zachování vstřícnosti a ochrany soukromého zájmu.', 'a', 'Otázka opravena OK, odpověď OK\r'),
(120, 'Smyslem služebního předpisu o pravidlech etiky státních zaměstnanců není:', 'Inspirovat, tedy napomáhat státním zaměstnancům osvojit si žádoucí vlastnosti a hodnoty spojené s jejich veřejnou rolí a profesní ctí.', 'Orientovat, tedy napomáhat státním zaměstnancům správně se rozhodovat v eticky náročných situacích.', 'Trestat, tedy stanovit tresty za překročení mezí chování státních zaměstnanců, které by bylo neslučitelné s posláním a hodnotami státní služby.', 'c', 'Otázka opravena OK, odpověď OK\r'),
(121, 'Zákon o státní službě stanoví státnímu zaměstnanci povinnost dodržovat pravidla etiky státních zaměstnanců vydaná:', 'Služebním předpisem.', 'Vyhláškou Ministerstva vnitra.', 'Nařízením vlády.', 'a', 'Otázka opravena OK, odpověď OK\r'),
(122, 'Při výkonu služby státní zaměstnanec zachovává věrnost:', 'Evropské unii.', 'Služebnímu orgánu.', 'České republice.', 'c', 'Otázka opravena OK, odpověď OK\r'),
(123, 'Představený ve smyslu zákona o státní službě:', 'Nesmí po dobu trvání služebního poměru vykonávat žádnou funkci v politické straně nebo politickém hnutí.', 'Může vykonávat funkci v politickém hnutí, nikoli však funkci v politické straně.', 'Může vykonávat funkci v politické straně, pokud je výkon této funkce bezplatný.', 'a', 'Otázka opravena OK, odpověď OK\r'),
(124, 'Státní zaměstnanec požádal svého spolupracovníka, aby za něj vypracoval služební úkol, který mu uložil jeho představený. Je takovýto postup možný?', 'Ano, bude-li spolupracovník za zpracování služebního úkolu odpovídajícím způsobem odměněn.', 'Ne, státní zaměstnanec je povinen plnit služební úkoly osobně.', 'Ano, v případě, že se o tomto postupu nedozví představený státního zaměstnance.', 'b', 'Otázka opravena OK, odpověď OK\r'),
(125, 'Dle zákona o státní službě:', 'Představený nesmí být členem odborové organizace.', 'Představenému nepřísluší vykonávat právo na stávku.', 'Představený nesmí být členem politické strany.', 'b', 'Otázka opravena OK, odpověď OK\r'),
(126, 'Ode dne zproštění výkonu služby na návrh kárné komise přísluší státnímu zaměstnanci plat ve výši:', '80 % jeho měsíčního platu.', '50 % jeho měsíčního platu.', '50 % jeho měsíčního platu, nejméně však ve výši minimální mzdy podle jiného zákona; tato část platu se zvýší o 10 % jeho měsíčního platu na každou státním zaměstnancem vyživovanou osobu, nejvýše však do výše 80 % jeho měsíčního platu.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(127, 'Systemizace neobsahuje:', 'Počet služebních míst představených klasifikovaných platovými třídami.', 'Počet služebních míst, u nichž je stanoven zákaz konkurence.', 'Sídlo služebního úřadu a jeho organizačních útvarů.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(128, 'Státní zaměstnanec nesmí splnit příkaz:', 'Spáchal-li by jeho splněním trestný čin nebo správní delikt.', 'Má-li za to, že příkaz je v rozporu s právním předpisem nebo služebním předpisem.', 'Má-li za to, že příkaz nepatří do oboru služby, v němž vykonává službu.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(129, 'Délka zkušební doby dle zákona o státní službě činí:', '3 měsíce.', '6 měsíců.', '6 měsíců u představených a 3 měsíce u ostatních státních zaměstnanců.', 'b', 'Otázka OK, odpověď OK\r'),
(130, 'Zástupce služebního orgánu nemá pravomoc rozhodovat ve věci:', 'Povolení kratší služební doby.', 'Jmenování na služební místo představeného.', 'Zařazení mimo výkon služby z organizačních důvodů.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(131, 'Dle zákona o státní službě označení služebních míst představených v ministerstvech a v Úřadu vlády jsou:', 'Vrchní ředitel sekce, ředitel odboru, vedoucí oddělení.', 'Ředitel odboru, vedoucí oddělení, vedoucí útvaru.', 'Ředitel sekce, ředitel odboru, vedoucí oddělení.', 'a', 'Otázka opravena OK, odpověď OK\r'),
(132, 'Státní zaměstnanec se domnívá, že příkaz k výkonu služby, který obdržel, je v rozporu se služebním předpisem. Státní zaměstnanec:', 'Není oprávněn soulad příkazu se služebním předpisem posuzovat.', 'Je oprávněn bez dalšího odmítnout splnění příkazu.', 'Je povinen na rozpor upozornit představeného dříve, než začne příkaz plnit.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(133, 'Služební předpis stanoví dle zákona o státní službě:', 'Organizační věci služby, vydává se v písemné podobě.', 'Příkazy k výkonu služby, vydává se v písemné podobě.', 'Organizační věci služby, nemusí být vydán v písemné podobě.', 'a', 'Otázka OK, odpověď OK\r'),
(134, 'Služební orgán může obsadit služební místo osobou v pracovním poměru na dobu určitou podle pracovněprávních předpisů, jestliže státní zaměstnanec dočasně nevykonává službu na služebním místě po dobu delší než:', '6 kalendářních měsíců.', '1 kalendářní měsíc.', '2 kalendářní měsíce.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(135, 'Dle zákona o státní službě služební slib skládá státní zaměstnanec vždy:', 'V den před nástupem do služby.', 'V první den kalendářního měsíce, ve kterém je státní zaměstnanec přijímán do služby.', 'V den nástupu do služby.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(136, 'Rejstřík státních zaměstnanců neobsahuje tento údaj o státním zaměstnanci:', 'Rodné číslo státního zaměstnance.', 'Datum narození státního zaměstnance.', 'Státní občanství státního zaměstnance.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(137, 'Sekce pro státní službu:', 'Je organizační jednotkou Ministerstva vnitra.', 'Je organizační jednotkou Úřadu vlády.', 'Je ústředním správním úřadem.', 'a', 'Otázka OK, odpověď OK\r'),
(138, 'Mezi kárná opatření podle zákona o státní službě nepatří:', 'Písemná důtka.', 'Pokuta.', 'Propuštění ze služebního poměru.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(139, 'Vydání rozhodnutí nemůže být prvním úkonem v řízení v případě rozhodování o:', 'Odvolání ze služebního místa představeného.', 'Zařazení mimo výkon služby z organizačních důvodů.', 'Skončení služebního poměru.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(140, 'Prohlubování vzdělání (vstupní vzdělávání, průběžné vzdělávání, vzdělávání představených a jazykové vzdělávání) dle zákona o státní službě koná státní zaměstnanec:', 'Na vlastní náklady, prohlubování vzdělání není výkonem služby a nepřísluší za něj tedy ani plat.', 'Na náklady služebního úřadu, prohlubování vzdělání se považuje za výkon služby a přísluší za něj státnímu zaměstnanci plat.', 'Na náklady služebního úřadu, prohlubování vzdělání se však nepovažuje za výkon služby a nepřísluší za něj státnímu zaměstnanci plat.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(141, 'Výběrové řízení na obsazení volného služebního místa nebo volného služebního místa představeného vyhlašuje služební orgán:', 'Na úřední desce, dále se zveřejní v informačním systému o státní službě.', 'Pouze na úřední desce.', 'Pouze v informačním systému o státní službě.', 'a', 'Otázka OK, odpověď OK\r'),
(142, 'Ke zrušení služebního poměru služebním orgánem nebo státním zaměstnancem ve zkušební době dojde:', 'Dnem doručení písemného oznámení o zrušení služebního poměru, není-li v něm uveden den pozdější.', 'Posledním dnem kalendářního měsíce, v němž bylo písemné oznámení o zrušení služebního poměru doručeno.', 'Do 30 dnů od doručení oznámení o zrušení služebního poměru.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(143, 'Kárná odpovědnost státního zaměstnance za kárné provinění zaniká, jestliže:', 'Uplynul jeden rok od jeho spáchání.', 'Do jednoho roku od jeho spáchání nebylo zahájeno kárné řízení.', 'Do jednoho roku od jeho spáchání nebylo kárné řízení pravomocně skončeno.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(144, 'Státního zaměstnance lze v zákonem stanovených případech zařadit mimo výkon služby z organizačních důvodů, nejdéle však na:', '6 měsíců.', '9 měsíců.', '12 měsíců.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(145, 'Státní zaměstnanec byl neúspěšný u úřednické zkoušky. Služební orgán bude vůči tomuto státnímu zaměstnanci postupovat následovně:', 'Umožní mu její opakovaní automaticky bez jeho žádosti. Úřednickou zkoušku může státní zaměstnanec opakovat dvakrát, termíny opakovaného konání určuje služební orgán.', 'Umožní mu její opakování na základě jeho písemné žádosti. Úřednickou zkoušku může státní zaměstnanec opakovat jen jednou, termín opakovaného konání stanoví služební orgán s přihlédnutím k žádosti státního zaměstnance.', 'Umožní mu její opakování na základě jeho písemné žádosti. Úřednickou zkoušku může státní zaměstnanec opakovat třikrát za obě její části dohromady. Termín prvního opakovaného konání určuje služební orgán, zbývající termíny si určuje sám státní zaměstnanec.', 'b', 'Otázka OK, odpověď OK\r'),
(146, 'Odborová organizace není oprávněna projednat a zaujmout stanovisko:', 'K návrhům služebního hodnocení státního zaměstnance.', 'K návrhům služebních předpisů.', 'K návrhům rozhodnutí týkajících se změn a skončení služebního poměru.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(147, 'Pravomocné odsouzení státního zaměstnance pro úmyslný trestný čin má za následek:', 'Skončení služebního poměru.', 'Zařazení mimo výkon služby.', 'Zproštění výkonu služby.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(148, 'Po dobu zařazení mimo výkon služby pro pozastavení služby:', 'Činí plat státního zaměstnance 60 % měsíčního platu.', 'Činí plat státního zaměstnance 80 % měsíčního platu.', 'Nepřísluší státnímu zaměstnanci plat.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(149, 'Kárným proviněním je:', 'Zaviněné porušení služební kázně.', 'Pouze úmyslné porušení služební kázně.', 'Pouze hrubé porušení služební kázně z nedbalosti.', 'a', 'Otázka OK, odpověď OK\r'),
(150, 'Existuje v zákoně o státní službě nějaké omezení politických práv státních zaměstnanců?', 'Ano, státní zaměstnanci nemohou být členy politických stran a hnutí a nesmí v nich vykonávat žádnou funkci.', 'Ne, všichni státní zaměstnanci mohou být členy politických stran a hnutí a vykonávat v nich funkce bez jakéhokoliv omezení.', 'Ano, státní zaměstnanci, kteří jsou představenými, nesmí vykonávat žádnou funkci v politické straně nebo v politickém hnutí.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(151, 'Odvolacím orgánem ve věcech kárné odpovědnosti je:', 'Nejvyšší státní tajemník.', 'Kárná komise druhého stupně.', 'Vedoucí služebního úřadu.', 'b', 'Otázka opravena OK, odpověď OK\r'),
(152, 'Výběrové řízení na obsazení volného služebního místa se zpravidla dokončí:', 'Do 60 dnů ode dne uplynutí lhůty pro podávání žádostí.', 'Do 90 dnů ode dne uplynutí lhůty pro podávání žádostí.', 'Do 90 dnů ode dne vyhlášení výběrového řízení.', 'a', 'Otázka opravena OK, odpověď OK\r'),
(153, 'Státní zaměstnanec byl uznán dočasně neschopným k výkonu služby. Platí, že:', 'V době prvních 30 dnů dočasné neschopnosti k výkonu služby mu za dny, které jsou dny výkonu služby, přísluší plat ve výši 60%.', 'V době prvních 14 dnů dočasné neschopnosti k výkonu služby za dny, které jsou dny výkonu služby, se státnímu zaměstnanci plat nekrátí.', 'V době prvních 14 dnů dočasné neschopnosti k výkonu služby mu za dny, které jsou dny výkonu služby, přísluší plat ve výši 60%.', 'c', 'Otázka OK, odpověď OK\r');
INSERT INTO `uz` (`id`, `otazka`, `odpoved_a`, `odpoved_b`, `odpoved_c`, `spravna_odpoved`, `status`) VALUES
(154, 'Osobou řídící činnosti související se zajišťováním organizačních věcí služby, správy služebních vztahů a odměňování státních zaměstnanců je dle zákona o státní službě:', 'V případě ministerstva a Úřadu vlády státní tajemník a v případě jiného služebního úřadu vedoucí služebního úřadu.', 'V případě ministerstva ministr a v případě jiného služebního úřadu vedoucí služebního úřadu.', 'V případě ministerstva státní tajemník a v případě jiného služebního úřadu ředitel odboru.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(155, 'Za kárné provinění nelze dle zákona o státní službě uložit kárné opatření spočívající v:', 'Odvolání ze služebního místa představeného.', 'Snížení platu až o 15 % až na dobu až 3 kalendářních měsíců.', 'Odebrání osobního příplatku až na dobu 3 kalendářních měsíců.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(156, 'Je možno, aby státní zaměstnanec dle zákona o státní službě vykonával dočasně službu v jiném služebním úřadu nebo v jiném organizačním útvaru svého služebního úřadu?', 'Ne, státní zaměstnanec vykonává službu vždy pouze na služebním místě, na kterém je zařazen nebo jmenován.', 'Ano, státního zaměstnance je možno přeložit na nezbytně nutnou dobu k výkonu služby v oboru služby, který vykonává. Tato doba je zákonem o státní službě časově omezena.', 'Ano, je možno přeložit státního zaměstnance na nezbytně nutnou dobu k výkonu služby i v oboru služby, který nevykonává. Tato doba je zákonem o státní službě časově neomezena.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(157, 'Podle zákona o státní službě je státní zaměstnanec povinen zachovávat mlčenlivost o skutečnostech, o nichž se dozvěděl při výkonu služby, a které v zájmu služebního úřadu nelze sdělovat jiným osobám. O povinnosti zachovávat mlčenlivost o služebních věcech ale neplatí, že:', 'Jí může státního zaměstnance zprostit služební orgán.', 'Jí může státního zaměstnance zprostit kárná komise.', 'Jí je povinen státní zaměstnanec dodržovat, i když nevykonává službu.', 'b', 'Otázka OK, odpověď OK\r'),
(158, 'Zkušební doba zaměstnance může být dle zákoníku práce sjednána nejvýše v délce:', '3 měsíce, avšak ne déle než je polovina sjednané doby trvání pracovního poměru.', '4 měsíce po sobě jdoucí ode dne vzniku pracovního poměru.', '6 měsíců, u vedoucího zaměstnance nejvýše v délce 9 měsíců.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(159, 'Podstatnou náležitostí pracovní smlouvy je:', 'Ujednání o pracovní době.', 'Výše odměny za práci.', 'Druh práce.', 'c', 'Otázka opravena OK, odpověď opravena OK (samo, bylo b)\r'),
(160, 'Občanský zákoník se v pracovněprávních vztazích:', 'Použije podpůrně (subsidiárně) vůči zákoníku práce za předpokladu dodržení základních zásad pracovněprávních vztahů.', 'Nikdy nepoužije.', 'Použije jen tam, kde to zákoník práce výslovně připouští.', 'a', 'Otázka opravena OK, odpověď opravena OK (samo, bylo c)\r'),
(161, 'Zaměstnavatel může dát zaměstnanci výpověď z pracovního poměru pouze z důvodů uvedených v § 52 zákoníku práce. Který z následujících důvodů mezi ně nepatří?', 'Ruší-li se zaměstnavatel nebo jeho část.', 'Stane-li se zaměstnanec nadbytečným vzhledem k rozhodnutí zaměstnavatele nebo příslušného orgánu o změně jeho úkolů, technického vybavení, o snížení stavu zaměstnanců za účelem zvýšení efektivnosti práce nebo o jiných organizačních změnách.', 'Nelze-li po zaměstnavateli spravedlivě požadovat, aby zaměstnance nadále zaměstnával.', 'c', 'Otázka OK, odpověď OK\r'),
(162, 'Způsobí-li zaměstnanec zaměstnavateli při výkonu práce úmyslně škodu, je povinen:', 'Nahradit celou vzniklou škodu.', 'Nahradit vzniklou škodu nejvýše do čtyřapůnásobku jeho průměrného měsíčního výdělku.', 'Nahradit vzniklou škodu nejvýše do pětinásobku jeho průměrného měsíčního výdělku.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(163, 'Které z následujících tvrzení týkající se dohody o rozvázání pracovního poměru není pravdivé?', 'Dohoda o rozvázání pracovního poměru musí být písemná.', 'Dohoda o rozvázání pracovního poměru nemůže být uzavřena s těhotnou zaměstnankyní.', 'Pracovní poměr končí dnem sjednaným v dohodě o rozvázání pracovního poměru.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(164, 'Na základě dohody o provedení práce je možno konat práci pro téhož zaměstnavatele v kalendářním roce nejvýše do:', '150 hodin.', '250 hodin.', '300 hodin.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(165, 'Zaměstnanci, který vykonává zaměstnání v pracovním poměru, vzniká za podmínek stanovených v Části deváté zákoníku práce právo na:', 'Dovolenou za kalendářní rok nebo její poměrnou část a dále právo na dodatkovou dovolenou.', 'Dovolenou za kalendářní rok nebo na její poměrnou část, dovolenou za odpracované dny a dále právo na dodatkovou dovolenou.', 'Dovolenou.', 'a', 'Otázka opravena OK, odpověď OK\r'),
(166, 'U zaměstnance se sjednanou kratší pracovní dobou práci přesčas:', 'Zaměstnavatel může jednostranně nařídit nebo s ním dohodnout.', 'Zaměstnavatel může se zaměstnancem pouze dohodnout.', 'Zákoník práce výslovně zakazuje.', 'b', 'Otázka opravena OK, odpověď OK (samo)\r'),
(167, 'Přestávku v práci na jídlo a oddech musí poskytnout zaměstnavatel zaměstnanci staršímu 18 let nejdéle po:', '4,5 hodinách nepřetržité práce v trvání nejméně 30 minut.', '5 hodinách nepřetržité práce v trvání nejméně 30 minut.', '6 hodinách nepřetržité práce v trvání nejméně 30 minut.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(168, 'Ohledně podmínek ubytování zaměstnance zaměstnavatele z jiného členského státu Evropské unie vyslaného k výkonu práce v rámci nadnárodního poskytování služeb na území České republiky, pokud jej zaměstnavatel poskytuje zaměstnanci, se na něho právní úprava České republiky vztahuje:', 'Jen pokud nejsou práva vyplývající z právních předpisů členského státu Evropské unie, z něhož byl zaměstnanec vyslán k výkonu práce v rámci nadnárodního poskytování služeb, pro něho výhodnější.', 'Vždy.', 'Pokud s tím zaměstnanec souhlasí.', 'a', 'Otázka opravena OK, odpověď OK (samo)\r'),
(169, 'Postup při uzavírání kolektivní smlouvy včetně řešení sporů mezi smluvními stranami se řídí:', 'Zákoníkem práce.', 'Občanským zákoníkem.', 'Zákonem o kolektivním vyjednávání.', 'c', 'Otázka OK, odpověď OK\r'),
(170, 'Peněžité plnění poskytované zaměstnanci za práci vykonanou na základě dohody o provedení práce se podle zákoníku práce nazývá:', 'Mzda.', 'Plat.', 'Odměna z dohody.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(171, 'Zastupování nepřítomného zaměstnance na témže sdíleném pracovním místě:', 'Může zaměstnavatel požadovat pouze tehdy, dal-li k tomu zaměstnanec v dohodě generální nebo pro konkrétní případ souhlas.', 'Je vyloučeno.', 'Může zaměstnavatel požadovat tehdy, dohodnou-li se na něm dva nebo více zaměstnanců s kratší pracovní dobou a se stejným druhem práce.', 'a', 'Otázka opravena OK, odpověď opravena OK (samo, bylo c)\r'),
(172, 'Byl-li nárok na náhradu škody podle zákona o odpovědnosti za škodu způsobenou při výkonu veřejné moci rozhodnutím nebo nesprávným úředním postupem uplatněn u úřadu, který není příslušný, tento úřad:', 'Postoupí žádost příslušnému úřadu.', 'Nárok zamítne.', 'Řízení převezme a o nároku rozhodne.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(173, 'Podle zákona o odpovědnosti za škodu způsobenou při výkonu veřejné moci rozhodnutím nebo nesprávným úředním postupem se odpovědnosti za způsobenou škodu:', 'Nelze zprostit.', 'Lze zprostit, prokáže-li stát, že vzniku škody nešlo zabránit ani postupem s péčí řádného hospodáře.', 'Lze zprostit, prokáže-li stát, že vznikem škody dosáhl stát majetkového prospěchu značného rozsahu.', 'a', 'Otázka OK, odpověď OK\r'),
(174, 'Ve věcech náhrady škody podle zákona o odpovědnosti za škodu způsobenou při výkonu veřejné moci rozhodnutím nebo nesprávným úředním postupem jménem státu jednají:', 'Obecní a krajské úřady.', 'Ministerstva a jiné ústřední správní úřady.', 'Zastupitelstva územních samosprávných celků.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(175, 'Osoba, kterou jsou členové petičního výboru povinni určit k zastupování petičního výboru ve styku se státními orgány, musí být:', 'Starší 16 let.', 'Starší 18 let.', 'Bezúhonná a starší 18 let.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(176, 'Ve výkonu petičního práva:', 'Nesmí být nikomu bráněno.', 'Může být přiměřenými omezeními bráněno osobám, které nemají občanství České republiky.', 'Může být přiměřenými omezeními bráněno osobám, které nemají občanství Evropské unie.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(177, 'Je-li státnímu orgánu adresována petice ve věci, která nepatří do jeho působnosti:', 'Státní orgán petici skartuje.', 'Státní orgán petici do 5 dnů postoupí státnímu orgánu, do jehož působnosti věc patří, a uvědomí o tom toho, kdo petici podal.', 'Státní orgán do 90 dnů uvědomí toho, kdo petici podal, že petice z tohoto důvodu nemohla být vyřízena.', 'b', 'Otázka OK, odpověď OK\r'),
(178, 'Petice je zákonem o právu petičním vymezena jako:', 'Žádost, návrh nebo stížnost ve věci veřejného nebo jiného společného zájmu.', 'Zvláštní forma odvolání proti rozhodnutí správního úřadu.', 'Podnět skupiny občanů adresovaný Parlamentu.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(179, 'Podpisové archy k podpoře petice:', 'Musí obsahovat úředně ověřené podpisy osob, které petici podpořily.', 'Musí obsahovat text petice, nebo musí být označeny tak, aby bylo zřejmé, jaká petice má být podpisy podpořena.', 'Mohou být vystaveny na místech přístupných veřejnosti pouze na základě povolení státního orgánu.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(180, 'Korporace je:', 'Právnická osoba vytvářená společenstvím osob.', 'Majetek vyčleněný k určitému účelu.', 'Každá právnická osoba, která je podnikatelem.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(181, 'Právní osobnost:', 'Je způsobilost nabývat pro sebe vlastním právním jednáním práva a zavazovat se k povinnostem.', 'Je způsobilost mít v mezích právního řádu práva a povinnosti.', 'Má pouze plně svéprávný člověk.', 'b', 'Otázka OK, odpověď OK\r'),
(182, 'Je zakázáno založit právnickou osobu:', 'Podporující násilí.', 'Za účelem šíření určitého náboženství.', 'Za účelem ovlivnění složení zákonodárných sborů.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(183, 'Věcí v právním smyslu je:', 'Vše, vyjma energií, přírodních sil a zvířat.', 'Vše, co není fyzická osoba.', 'Vše, co je rozdílné od osoby a slouží potřebě lidí.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(184, 'Závdavkem se podle občanského zákoníku rozumí:', 'Dar uskutečněný mezi budoucími manželi evidovaný v předmanželské smlouvě.', 'Plnění potvrzující uzavření smlouvy a poskytnutí jistoty, že dluh bude splněn.', 'Zvláštní druh věcného břemena spočívající ve zřízení společného nájmu bytu nebo domu manželi.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(185, 'Právo stavby:', 'Nesmí být zřízeno na více než 99 let.', 'Nesmí být zřízeno na více než 20 let.', 'Může být zřízeno na neomezenou dobu.', 'a', 'Otázka OK, odpověď OK\r'),
(186, 'Podle občanského zákoníku je důvodem pro rozvod manželství:', 'Výhradně hluboce, trvale a nenapravitelně rozvrácené soužití manželů.', 'Mezi jinými i duševní porucha, která není jen přechodná.', 'Mezi jinými i ztráta reprodukčních schopností doložená posudkem soudního znalce.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(187, 'Dědí se na základě:', 'Prohlášení dědiců, zákonné posloupnosti nebo rozhodnutí soudu.', 'Dědické smlouvy, ze závěti nebo ze zákona.', 'Závěti nebo rozhodnutí soudního komisaře.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(188, 'Vyvlastnit věc lze podle občanského zákoníku:', 'Ve stavu nouze nebo v naléhavém veřejném zájmu.', 'Ve veřejném zájmu a jen na základě rozhodnutí soudu.', 'Ve veřejném zájmu, který nelze uspokojit jinak, a jen na základě zákona.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(189, 'Nadace:', 'Může být založena za účelem podpory politických stran a hnutí.', 'Nemůže být založena za účelem podpory politických stran a hnutí.', 'Může být založena výlučně pro výdělečné cíle, ale ne za účelem podpory politických hnutí.', 'b', 'Otázka opravena OK, odpověď OK\r'),
(190, 'Kupní smlouvou:', 'Se prodávající zavazuje, že kupujícímu odevzdá věc, která je předmětem koupě, a umožní mu nabýt vlastnické právo k ní, a kupující se zavazuje, že věc převezme a zaplatí prodávajícímu kupní cenu.', 'Se prodávající zavazuje kupujícímu přenechat věc k užívání, a kupující se zavazuje věc řádně užívat.', 'Se kupující nezavazuje, že věc převezme.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(191, 'Byl-li údaj zapsaný ve veřejném rejstříku zveřejněn, nemůže se nikdo dovolat, že o zveřejněném údaji nemohl vědět:', 'Po uplynutí sedmi dnů od zveřejnění.', 'Po uplynutí patnácti dnů od zveřejnění.', 'Po uplynutí třiceti dnů od zveřejnění.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(192, 'Podle občanského zákoníku se kvitancí rozumí:', 'Vykonatelný rozsudek soudu prvního stupně.', 'Potvrzení o splnění dluhu.', 'Výpis z veřejného rejstříku.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(193, 'Obecné nařízení o ochraně osobních údajů stanoví, že subjekt údajů:', 'Musí za určitých okolností souhlas se zpracováním svých osobních údajů správci nebo zpracovateli udělit.', 'Nemůže jednou udělený souhlas odvolat.', 'Může udělit souhlas se zpracováním svých osobních údajů a kdykoli jej odvolat.', 'c', 'Otázka OK, odpověď OK\r'),
(194, 'Podle obecného nařízení o ochraně osobních údajů může správce zpracovávat osobní údaje:', 'Pouze, pokud je schopen subjekt údajů jednoznačně identifikovat.', 'I když není schopen subjekt údajů jednoznačně identifikovat.', 'Pouze pokud osobní údaje pseudonymizuje.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(195, 'Podle obecného nařízení o ochraně osobních údajů lze legitimně zpracované osobní údaje předat do jiného členského státu Evropské unie:', 'Bez omezení.', 'Po ohlášení Úřadu pro ochranu osobních údajů.', 'Po ohlášení Evropskému sboru pro ochranu osobních údajů.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(196, 'Podle obecného nařízení o ochraně osobních údajů je správce povinen dokumentovat:', 'Zjištěné porušení zabezpečení osobních údajů, způsobené zvnějšku.', 'Pouze závažné porušení, které současně ohlašuje Úřadu pro ochranu osobních údajů.', 'Jakékoli zjištěné porušení zabezpečení osobních údajů.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(197, 'Podle obecného nařízení o ochraně osobních údajů je šifrování osobních údajů:', 'Vhodným opatřením k zabezpečení automatizovaně zpracovávaných osobních údajů.', 'Povinným opatřením k zabezpečení automatizovaně zpracovávaných osobních údajů.', 'Povinným opatřením k zabezpečení externích datových médií.', 'a', 'Otázka OK, odpověď OK\r'),
(198, 'Podle obecného nařízení o ochraně osobních údajů není právem člověka jako subjektu údajů:', 'Právo podat stížnost u dozorového úřadu.', 'Právo získat na žádost originál osobních údajů, které poskytl určitému správci.', 'Právo na to, aby správce bez zbytečného odkladu opravil jeho nepřesné osobní údaje.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(199, 'Podle zákona o zpracování osobních údajů se subjektem údajů rozumí:', 'Právnická osoba, k níž se osobní údaje vztahují.', 'Fyzická osoba, k níž se osobní údaje vztahují.', 'Fyzická osoba, která dala souhlas ke zpracování svých údajů.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(200, 'Zákon o zpracování osobních údajů stanoví, že sídlem Úřadu pro ochranu osobních údajů je:', 'Brno.', 'Praha.', 'Česká republika.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(201, 'Zákon o zpracování osobních údajů:', 'Stanoví kvalifikační požadavky na pověřence pro ochranu osobních údajů s působností pro Českou republiku.', 'Stanoví kvalifikační požadavky na pověřence pro ochranu osobních údajů jmenované veřejnými subjekty působícími v České republice.', 'Nestanoví kvalifikační požadavky na pověřence pro ochranu osobních údajů.', 'c', 'Otázka OK, odpověď OK\r'),
(202, 'Zákon o zpracování osobních údajů, stanoví, že spravujícím orgánem je orgán veřejné moci příslušný k plnění úkolu za účelem předcházení, vyhledávání a odhalování trestné činnosti, stíhání trestných činů, výkonu trestů a ochranných opatření, zajišťování bezpečnosti České republiky nebo veřejného pořádku a vnitřní bezpečnosti, včetně pátrání po osobách a věcech,:', 'Včetně zpravodajské služby a obecní policie.', 'Není-li zpravodajskou službou nebo obecní policií.', 'S výjimkou soudů všech stupňů a státního zastupitelství.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(203, 'Kybernetickou bezpečnostní událostí podle zákona o kybernetické bezpečnosti:', 'Jsou pravidelná setkání národního a vládního CERT. Jedná se o významnou bezpečnostní událost strategického charakteru nezbytnou pro zajišťování kybernetické bezpečnosti.', 'Jsou pravidelné reporty o kybernetických incidentech. Jedná se o bezpečnostní událost politického charakteru nezbytnou pro Bezpečnostní radu státu.', 'Je událost, která může způsobit narušení bezpečnosti informací v informačních systémech nebo narušení bezpečnosti služeb anebo bezpečnosti a integrity sítí elektronických komunikací.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(204, 'Kritickou informační infrastrukturou podle zákona o kybernetické bezpečnosti rozumíme:', 'Informační systémy, na nichž jsou vytvářeny, uchovávány a přenášeny utajované informace.', 'Veškeré informační systémy fungující a operující na území České republiky. Dohromady tvoří síť kritických systémů stěžejních pro suverénní fungování státu.', 'Prvek nebo systém prvků kritické infrastruktury v odvětví komunikační a informační systémy v oblasti kybernetické bezpečnosti.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(205, 'Vládní CERT podle zákona o kybernetické bezpečnosti:', 'Vyhodnocuje údaje o kybernetických bezpečnostních událostech a kybernetických bezpečnostních incidentech z kritické informační infrastruktury, informačního systému základní služby, významných informačních systémů a dalších informačních systémů veřejné správy.', 'Předkládá kontrolnímu orgánu zprávu o činnosti Národního úřadu pro kybernetickou a informační bezpečnost. Zpráva o činnosti Národního úřadu pro kybernetickou a informační bezpečnost je výhradně technického rázu, jelikož kybernetická bezpečnost je ryze technickým tématem. Vládní CERT zde zodpovídá za informovanost čelních představitelů ČR v oblasti kybernetické bezpečnosti.', 'Přijímá oznámení kontaktních údajů od poskytovatelů digitální služby a poskytovatelů služby elektronických komunikací a tyto údaje eviduje a uchovává.', 'a', 'Otázka OK, odpověď OK\r'),
(206, 'Který ústřední správní orgán se zabývá oblastí kybernetické bezpečnosti?', 'Národní bezpečnostní úřad.', 'Bezpečnostní informační služba.', 'Národní úřad pro kybernetickou a informační bezpečnost.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(207, 'Bezpečností informací se podle zákona o kybernetické bezpečnosti rozumí:', 'Zajištění důvěrnosti, integrity a dostupnosti informací a dat.', 'Zajištění správnosti a neporušenosti informací a dat.', 'To, že data nejsou infikovaná virem.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(208, 'Varování podle zákona o kybernetické bezpečnosti:', 'Je zveřejněno na internetových stránkách Národního úřadu pro kybernetickou a informační bezpečnost a oznámeno povinným osobám dle zákona o kybernetické bezpečnosti.', 'Je provedeno jednotným systémem varování a informování.', 'Je provedeno sdělovacími prostředky (rozhlasem, televizí, místním rozhlasem), tzv. mluvícími sirénami, vozidly složek integrovaného záchranného systému.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(209, 'Mezi povinnosti, které provozovatelé základních služeb musí podle zákona o kybernetické bezpečnosti po určení plnit, patří:', 'Předat Národnímu úřadu pro kybernetickou a informační bezpečnost veškerá data.', 'Zaslat Národnímu úřadu pro kybernetickou a informační bezpečnost kontaktní údaje.', 'Zaslat Národnímu úřadu pro kybernetickou a informační bezpečnost plán rozvoje společnosti.', 'b', 'Otázka OK, odpověď OK\r'),
(210, 'Stavem kybernetického nebezpečí se podle zákona o kybernetické bezpečnosti rozumí:', 'narušení bezpečnosti informací v informačních systémech nebo narušení bezpečnosti služeb anebo bezpečnosti a integrity sítí elektronických komunikací v důsledku Kybernetické bezpečnostní události.', 'stav, ve kterém je ve velkém rozsahu ohrožena bezpečnost informací v informačních systémech nebo bezpečnost služeb elektronických komunikací anebo bezpečnost a integrita sítí elektronických komunikací, a tím by mohlo dojít k porušení nebo došlo k ohrožení zájmu České republiky ve smyslu zákona upravujícího ochranu utajovaných informací.', 'stav, kdy neohlásí správce nebo provozovatel informačního nebo komunikačního systému kritické informační infrastruktury nebo správce nebo provozovatel významného informačního systému kybernetický bezpečnostní incident podle § 8 odst. 1 a 4.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(211, 'Žaloba proti rozhodnutí správního orgánu podle zákona č. 150/2002 Sb., soudní řád správní, je nepřípustná, jestliže:', 'Jde o rozhodnutí správního orgánu v soukromoprávní věci, vydané v mezích zákonné pravomoci správního orgánu.', 'Směřuje jen proti výroku rozhodnutí.', 'Se žalobce domáhá zrušení rozhodnutí.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(212, 'Pokud zákon nestanoví jinak, je k řízení podle zákona č. 150/2002 Sb., soudní řád správní, věcně příslušný:', 'Krajský soud.', 'Okresní soud.', 'Nejvyšší správní soud.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(213, 'Žalobce může podat žalobu na ochranu před nezákonným zásahem, pokynem nebo donucením správního orgánu podle zákona č. 150/2002 Sb., soudní řád správní, ve lhůtě:', 'Šesti měsíců ode dne, kdy se žalobce dozvěděl o nezákonném zásahu, nejpozději však do dvou let od okamžiku, kdy k němu došlo.', 'Dvou měsíců ode dne, kdy se žalobce dozvěděl o nezákonném zásahu, nejpozději však do dvou let od okamžiku, kdy k němu došlo.', 'Dvou měsíců ode dne, kdy se žalobce dozvěděl o nezákonném zásahu, nejpozději však do pěti let od okamžiku, kdy k němu došlo.', 'b', 'Otázka OK, odpověď OK\r'),
(214, 'Člena Nejvyššího kontrolního úřadu může odvolat:', 'Prezident Nejvyššího kontrolního úřadu na základě pravomocného rozsudku, jímž byl člen odsouzen pro úmyslný trestný čin.', 'Poslanecká sněmovna na základě pravomocného rozhodnutí Kárné komory Nejvyššího kontrolního úřadu, kterým bylo navrženo odvolání z funkce.', 'Kárná komora Nejvyššího kontrolního úřadu za závažné kárné provinění nebo opakované kárné provinění.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(215, 'Ke zprávě o vývoji ekonomiky a plnění státního rozpočtu a k návrhu státního závěrečného účtu předkládaným vládou Poslanecké sněmovně:', 'Se Nejvyšší kontrolní úřad nevyjadřuje.', 'Nejvyšší kontrolní úřad vždy zpracovává a Poslanecké sněmovně předkládá stanovisko.', 'Zpracovává Nejvyšší kontrolní úřad stanovisko na vyžádání Poslanecké sněmovny nebo jejích orgánů.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(216, 'O námitkách kontrolované osoby proti kontrolnímu protokolu rozhoduje podle zákona o Nejvyšším kontrolním úřadu:', 'Senát Nejvyššího kontrolního úřadu.', 'Vedoucí skupiny kontrolujících.', 'Kolegium Nejvyššího kontrolního úřadu.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(217, 'Nejvyššímu kontrolnímu úřadu:', 'Nelze poskytovat údaje ze základního registru obyvatel, pokud se jedná o osobní údaje v rozsahu jméno a příjmení.', 'Lze poskytovat údaje ze základního registru občanů, s výjimkou referenčních.', 'Lze poskytovat referenční údaje  ze základního registru obyvatel.', 'c', 'Otázka OK, odpověď OK\r'),
(218, 'Prezidenta Nejvyššího kontrolního úřadu a viceprezidenta Nejvyššího kontrolního úřadu:', 'Nelze po dobu jejich funkčního období z funkce odvolat.', 'Může odvolat jen prezident republiky na návrh Poslanecké sněmovny.', 'Může odvolat předseda vlády na návrh kolegia Nejvyššího kontrolního úřadu.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(219, 'Prezident a viceprezident Nejvyššího kontrolního úřadu se ujímají funkce:', 'Jmenováním.', 'Zvolením.', 'Složením slibu.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(220, 'Nejvyšší kontrolní úřad nevykonává kontrolu:', 'Zadávání státních zakázek.', 'Plnění státního rozpočtu.', 'Zpracování tuzemských bankovek a tuzemských mincí.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(221, 'Nemůže-li prezident Nejvyššího kontrolního úřadu ani viceprezident Nejvyššího kontrolního úřadu po přechodnou dobu vykonávat svoji funkci, vykonává funkci prezidenta Nejvyššího kontrolního úřadu:', 'Kolegium Nejvyššího kontrolního úřadu.', 'Statutární zástupce podle jednacího řádu.', 'Nejstarší člen Nejvyššího kontrolního úřadu.', 'c', 'Otázka OK, odpověď OK\r'),
(222, 'Výroční zprávu předkládá prezident Nejvyššího kontrolního úřadu ke schválení:', 'Kolegiu Nejvyššího kontrolního úřadu.', 'Členům Nejvyššího kontrolního úřadu.', 'Poslanecké sněmovně.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(223, 'Schválené kontrolní závěry podle zákona o Nejvyšším kontrolním úřadu:', 'Nesmí být zveřejněny.', 'Zveřejňuje prezident Nejvyššího kontrolního úřadu ve Věstníku Nejvyššího kontrolního úřadu.', 'Jsou archivovány, jejich zveřejnění podléhá výlučně pravidlům zákona o archivnictví a spisové službě.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(224, 'Dle zákona č. 218/2000 Sb., o rozpočtových pravidlech a o změně některých souvisejících zákonů (rozpočtová pravidla), je na dotaci ze státního rozpočtu právní nárok, pokud:', 'Tak stanoví zvláštní právní předpis.', 'Se jedná o dotaci spolufinancovanou z rozpočtu Evropské unie.', 'Žádost příjemce obsahuje všechny předepsané náležitosti.', 'a', 'Otázka opravena OK, odpověď OK\r'),
(225, 'Jako peněžní fondy tvoří podle zákona č. 218/2000 Sb., o rozpočtových pravidlech a o změně některých souvisejících zákonů (rozpočtová pravidla) organizační složka státu:', 'Rezervní fond a fond kulturních a sociálních potřeb.', 'Rezervní fond a fond národního majetku.', 'Fond kulturních a sociálních potřeb a investiční fond.', 'a', 'Otázka OK, odpověď OK\r'),
(226, 'Za plnění státního rozpočtu odpovídá:', 'Ministerstvo financí vládě.', 'Vláda Poslanecké sněmovně.', 'Ministerstvo financí Rozpočtovému výboru Poslanecké sněmovny.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(227, 'V souladu se zákonem č. 218/2000  Sb., o rozpočtových pravidlech a o změně některých souvisejících zákonů (rozpočtová pravidla) finanční úřady vykonávají správu odvodů za porušení rozpočtové kázně a penále:', 'Podle daňového řádu.', 'Podle správního řádu.', 'Podle občanského soudního řádu.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(228, 'Stát:', 'Není právnickou osobou.', 'Je právnickou osobou, pokud vystupuje v právních vztazích.', 'Je právnickou osobou pouze z pohledu práva Evropské unie.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(229, 'Podle zákona č. 219/2000 Sb., o majetku České republiky a jejím vystupování v právních vztazích, lze právo stavby k pozemku ve vlastnictví státu zřídit:', 'Za úplatu; bezúplatně jen ve veřejném zájmu. Smluvní zřízení práva stavby vyžaduje schválení Ministerstvem financí.', 'Pouze bezúplatně. Smluvní zřízení práva stavby vyžaduje předchozí souhlas Ministerstva financí.', 'Pouze za úplatu. Smluvní zřízení práva stavby vyžaduje schválení příslušným ministerstvem.', 'a', 'Otázka OK, odpověď OK\r'),
(230, 'Na základě zákona č. 219/2000 Sb., o majetku České republiky a jejím vystupování v právních vztazích, je provozovatelem a správcem Centrálního registru administrativních budov:', 'Provozovatelem je Úřad pro zastupování státu ve věcech majetkových a správcem je Ministerstvo financí.', 'Provozovatelem i správcem je Úřad pro zastupování státu ve věcech majetkových.', 'Provozovatelem je Úřad pro zastupování státu ve věcech majetkových a správcem je Ministerstvo vnitra.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(231, 'Pro odebírání vzorků podle kontrolního řádu neplatí:', 'Odebírání vzorků je vždy bezplatné.', 'O každém odběru vzorku kontrolující vydává potvrzení.', 'Kontrolní orgán odebraný vzorek vrátí, je-li to možné.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(232, 'Přizvanou osobou je dle kontrolního řádu:', 'Zástupce kontrolovaného, který se účastní úkonů při kontrole na místě.', 'Osoba určená kontrolujícím, která se za účelem dosažení účelu kontroly na kontrole podílí.', 'Osoba vykonávající státní dozor nad výkonem kontroly.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(233, 'Kontrolující není podle kontrolního řádu oprávněn:', 'Požadovat po kontrolované osobě písemnou zprávu o odstranění nebo prevenci nedostatků zjištěných kontrolou.', 'Pořizovat obrazové nebo zvukové záznamy.', 'Požadovat po kontrolované osobě, aby na své náklady úředně ověřila kopie listin nezbytných pro dosažení účelu kontroly.', 'c', 'Otázka OK, odpověď OK\r'),
(234, 'Námitky proti kontrolnímu zjištění mohou být podány dle kontrolního řádu:', 'Nejpozději do 10 dnů ode dne doručení protokolu o kontrole, není-li stanovena v protokolu o kontrole lhůta delší.', 'Nejpozději do 15 dnů ode dne doručení protokolu o kontrole, není-li stanovena v protokolu o kontrole lhůta delší.', 'Nejpozději do 30 dnů ode dne doručení protokolu o kontrole, není-li stanovena v protokolu o kontrole lhůta delší.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(235, 'Opožděné námitky, námitky podané neoprávněnou osobou, námitky, z nichž není zřejmé, proti jakému kontrolnímu zjištění směřují, nebo námitky, u nichž chybí odůvodnění, nadřízená osoba kontrolujícího podle kontrolního řádu:', 'Odmítne.', 'Zamítne.', 'Odloží.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(236, 'Kontrola není podle kontrolního řádu ukončena:', 'Marným uplynutím lhůty pro podání námitek nebo vzdáním se práva podat námitky.', 'Dnem doručení vyřízení námitek kontrolované osobě.', 'Doručením protokolu o kontrole kontrolované osobě.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(237, 'Namítat podjatost kontrolujícího nebo přizvané osoby je/jsou podle kontrolního řádu oprávněna/oprávněny:', 'Pouze kontrolovaná osoba.', 'Kontrolovaná osoba a v zákonem stanovených případech též povinná osoba.', 'Všechny osoby zúčastněné na kontrole.', 'a', 'Otázka OK, odpověď OK\r'),
(238, 'Tzv. atrakce (převzetí výkonu kontroly nadřízeným správním orgánem) v režimu kontrolního řádu:', 'Není přípustná.', 'Není blíže upravena.', 'Je přípustná v odůvodněných případech.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(239, 'Přizvaná osoba má podle kontrolního řádu povinnost:', 'Podepsat protokol o kontrole.', 'Zachovávat mlčenlivost.', 'Vyřizovat námitky proti kontrolním zjištěním.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(240, 'Kontrola je podle kontrolního řádu kontrolním orgánem vždy zahajována:', 'Z moci úřední (ex offo).', 'Na žádost kontrolované nebo povinné osoby.', 'Na návrh třetí osoby.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(241, 'Zjistí-li kontrolující, že musí po ukončení kontroly učinit další potřebné kontrolní úkony, je oprávněn provést podle kontrolního řádu:', 'Obnovu kontroly.', 'Došetření věci.', 'Přezkumné řízení.', 'b', 'Otázka OK, odpověď OK\r'),
(242, 'Vyznačení stupně utajení na utajované informaci musí být zachováno:', 'Po celou dobu trvání důvodů utajení.', 'Jen do doby kompromitace utajované informace.', 'Po dobu 5 let.', 'a', 'Otázka opravena OK, odpověď OK\r'),
(243, 'Zabezpečenou oblast třídy I lze na dobu nezbytně nutnou změnit na zabezpečenou oblast třídy II:', 'V mimořádných situacích (jako je stav nebezpečí nebo nouzový stav) vždy.', 'Pouze v případě, kdy je zajištěno, že k utajované informaci nemá přístup neoprávněná osoba.', 'Není to možné, neboť již vstupem do zabezpečené oblasti třídy I dochází k seznámení s utajovanou informací.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(244, 'Taktickou informací podle zákona o ochraně utajovaných informací a o bezpečnostní způsobilosti je utajovaná informace:', 'Pro utajování bojových operací a taktických zákroků Armády České republiky.', 'S krátkou dobou trvání důvodu utajení.', 'S dlouhou dobou trvání důvodu utajení.', 'b', 'Otázka opravena OK, odpověď OK\r'),
(245, 'Se změnou nebo zrušením stupně utajení národní (české) utajované informace musí souhlasit:', 'Původce.', 'Bezpečnostní ředitel.', 'Odpovědná osoba.', 'a', 'Otázka OK, odpověď OK\r'),
(246, 'Povinnost zachovávat mlčenlivost o utajované informaci má:', 'Jen původce utajované informace.', 'Každý, kdo měl nebo má k ní přístup.', 'Jen fyzická osoba, která je držitelem osvědčení.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(247, 'Po zániku platnosti osvědčení fyzické osoby je tato fyzická osoba povinna:', 'Osvědčení fyzické osoby do 10 dnů skartovat za účasti bezpečnostního ředitele a vyrozumět o této skutečnosti Národní bezpečnostní úřad.', 'Odevzdat do 30 dnů osvědčení fyzické osoby Národnímu bezpečnostnímu úřadu.', 'Odevzdat do 15 dnů osvědčení fyzické osoby tomu, kdo toto osvědčení vydal.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(248, 'Při nálezu utajované informace je každý povinen:', 'Uschovat tuto utajovanou informaci, bez zbytečného odkladu kontaktovat Národní bezpečnostní úřad, Policii České republiky nebo zastupitelský úřad České republiky a vyčkat příjezdu kurýrní služby.', 'Neprodleně odevzdat tuto utajovanou informaci Národnímu bezpečnostnímu úřadu, Policii České republiky nebo zastupitelskému úřadu České republiky.', 'Vložit tuto utajovanou informaci do vnitřní obálky, na kterou vyznačí příslušný stupeň utajení, a následně do vnější neoznačené obálky a takto ji odeslat Národnímu bezpečnostnímu úřadu.', 'b', 'Otázka opravena OK, odpověď OK\r'),
(249, 'Ústřední registr utajovaných informací poskytovaných v mezinárodním styku vede:', 'Národní bezpečnostní úřad.', 'Národní úřad pro kybernetickou a informační bezpečnost.', 'Ministerstvo zahraničních věcí.', 'a', 'Otázka opravena OK, odpověď OK\r'),
(250, 'Správní orgán, který není věcně nebo místně příslušný k vyřízení došlého podání, bezodkladně:', 'Vyrozumí podatele o této skutečnosti a poučí ho o tom, na jaký správní orgán se má se svým podáním obrátit.', 'Podání usnesením postoupí příslušnému správnímu orgánu a současně o tom uvědomí podatele.', 'Podání odloží usnesením poznamenaným do spisu.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(251, 'Dle správního řádu se uložená písemnost považuje za doručenou, jestliže si ji adresát nevyzvedne:', 'Ve lhůtě 15 dnů ode dne, kdy byla uložena.', 'Ve lhůtě 10 dnů ode dne, kdy byla k vyzvednutí připravena.', 'Ve lhůtě 7 dnů ode dne, kdy byla k vyzvednutí připravena.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(252, 'Správní orgán může doručovat na e-mailovou adresu:', 'Na požádání účastníka řízení, zejména může-li to přispět k urychlení řízení.', 'Pouze v řízeních zahajovaných na žádost.', 'Pouze písemnosti, které nejsou určeny do vlastních rukou.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(253, 'Podle správního řádu mohou oprávněné osoby nahlížet do spisu:', 'Pouze do okamžiku, kdy rozhodnutí nabude právní moci.', 'V průběhu řízení i po nabytí právní moci rozhodnutí.', 'Jen v době po nabytí právní moci rozhodnutí.', 'b', 'Otázka OK, odpověď OK\r'),
(254, 'Nahlížet do spisu:', 'Mohou pouze účastníci řízení nebo jejich zástupci.', 'Mohou kromě účastníků nebo jejich zástupců i jiné osoby, prokáží-li právní zájem nebo jiný vážný důvod.', 'Mohou pouze oprávněné úřední osoby, které ve věci rozhodují.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(255, 'Nezaplatí-li žadatel ve stanovené lhůtě správní poplatek, k jehož zaplacení byl ve správním řízení povinen, správní orgán:', 'Podá obecnému správci daně návrh na provedení exekuce.', 'Žádost rozhodnutím zamítne.', 'Řízení o žádosti usnesením zastaví.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(256, 'Mezi zajišťovací prostředky ve správním řízení patří:', 'Předvolání, předvedení, předběžné opatření, pořádková pokuta, vykázání z místa úkonu a záruka za splnění povinnosti.', 'Předvolání, předvedení, opatření obecné povahy a pořádková pokuta.', 'Předvolání, předvedení, příkaz a vykázání z místa úkonu.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(257, 'Proti rozhodnutí odvolacího správního orgánu se dle správního řádu:', 'Lze odvolat.', 'Nelze odvolat.', 'Lze odvolat, jestliže odvolací správní orgán tímto rozhodnutím změnil rozhodnutí orgánu prvního stupně.', 'b', 'Otázka OK, odpověď OK\r'),
(258, 'Vezme-li žadatel svou žádost zpět, správní orgán:', 'Řízení usnesením zastaví.', 'Může pokračovat v řízení a vydat rozhodnutí, vyžaduje-li to veřejný zájem.', 'tuto skutečnost poznamená do spisu a nečiní v řízení další úkony.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(259, 'Dle správního řádu se při doručování veřejnou vyhláškou považuje písemnost za doručenou:', 'Sedmým dnem po vyvěšení.', 'Desátým dnem po vyvěšení.', 'Patnáctým dnem po vyvěšení.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(260, 'Odvolací správní orgán může napadené rozhodnutí:', 'zrušit, změnit nebo potvrdit.', 'pouze zrušit nebo potvrdit.', 'pouze změnit nebo potvrdit.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(261, 'Veřejnoprávní smlouvu lze dle správního řádu vypovědět:', 'Jen tehdy, bylo-li to ve veřejnoprávní smlouvě dohodnuto.', 'Pouze v případě, změní-li se podstatně poměry, které byly rozhodující pro stanovení obsahu smlouvy.', 'Jen tehdy, je-li to nutné z důvodu ochrany veřejného zájmu.', 'a', 'Otázka OK, odpověď OK\r'),
(262, 'V přezkumném řízení lze zrušit nezákonné rozhodnutí:', 'Do 1 roku ode dne právní moci rozhodnutí.', 'Do 15 měsíců ode dne právní moci rozhodnutí.', 'Do 3 let ode dne právní moci rozhodnutí.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(263, 'Ústní jednání je dle správního řádu:', 'Vždy neveřejné.', 'Veřejné, pokud správní orgán neurčí, že jednání nebo jeho část jsou neveřejné.', 'Neveřejné, pokud zákon nestanoví nebo správní orgán neurčí, že jednání nebo jeho část jsou veřejné.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(264, 'Proti usnesením nebo jiným úkonům exekučního správního orgánu, proti kterým se nelze odvolat, může povinný nebo jiná osoba, které z tohoto úkonu vyplývá povinnost, podat:', 'Námitky.', 'Stížnost.', 'Rozklad.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(265, 'Účastník správního řízení může mít v téže věci současně:', 'Pouze jednoho zmocněnce.', 'Více zmocněnců.', 'Více zmocněnců, pokud s tím správní orgán souhlasí.', 'a', 'Otázka OK, odpověď OK\r'),
(266, 'Odpor je řádným opravným prostředkem proti:', 'Usnesení.', 'Příkazu.', 'Rozhodnutí, které vydal ústřední správní úřad, ministr nebo vedoucí jiného ústředního správního úřadu v prvním stupni.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(267, 'Dle správního řádu se spis zakládá:', 'Pouze tehdy, rozhodne-li tak úřední osoba.', 'Pouze tehdy, je-li možné ve věci podat opravný prostředek.', 'V každé věci.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(268, 'Archivem podle zákona o archivnictví a spisové službě se rozumí:', 'Zařízení podle tohoto zákona, které slouží k dlouhodobému ukládání archiválií a péči o ně.', 'Zařízení nebo místnost, ve kterých veřejnoprávní nebo soukromoprávní původce dlouhodobě ukládá písemnosti vzešlé z jeho činnosti.', 'Zařízení shromažďující archiválie veřejnoprávních i soukromoprávních původců a osobností veřejného života, které pak na základě vlastních vnitřních předpisů poskytuje veřejnosti.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(269, 'Vnitřní předpis správního úřadu stanovující základní pravidla pro manipulaci s dokumenty a skartační řízení se nazývá:', 'Zákon o archivnictví a spisové službě.', 'Spisový řád.', 'Kancelářský řád.', 'b', 'Otázka OK, odpověď OK\r'),
(270, 'Pokus přestupku:', 'Je vždy trestný.', 'Není trestný.', 'Je trestný, pokud tak stanoví zákon.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(271, 'Mezi správní tresty nepatří:', 'Trest odnětí svobody.', 'Pokuta.', 'Zveřejnění rozhodnutí o přestupku.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(272, 'Ochrannými opatřeními jsou:', 'Omezující opatření a zabrání věci nebo náhradní hodnoty.', 'Zákaz činnosti a omezující opatření.', 'Propadnutí věci nebo náhradní hodnoty a zákaz činnosti.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(273, 'Řízení o přestupku je zahájeno:', 'Dnem, kdy oznámení o přestupku došlo správnímu orgánu.', 'Dnem, kdy byl založen spis v dané věci.', 'Doručením oznámení o zahájení řízení podezřelému z přestupku nebo ústním vyhlášením takového oznámení.', 'c', 'Otázka OK, odpověď OK\r'),
(274, 'Jestliže spáchání skutku, o němž se vede řízení, nebylo obviněnému prokázáno, správní orgán:', 'Vydá rozhodnutí o tom, že obviněný je nevinný.', 'Řízení usnesením zastaví.', 'Věc odloží.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(275, 'Obviněný v odvolání nebo v průběhu odvolacího řízení:', 'Může uvádět nové skutečnosti nebo důkazy.', 'Nemůže uvádět nové skutečnosti nebo důkazy.', 'Může uvádět nové skutečnosti nebo důkazy pouze tehdy, jde-li o takové skutečnosti nebo důkazy, které nemohl uplatnit dříve.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(276, 'Mezi orgány Evropské unie dle čl. 13 Smlouvy o Evropské unii patří:', 'Účetní dvůr.', 'Hospodářský a sociální výbor.', 'Výbor regionů.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(277, 'Účetní dvůr provádí kontrolu účetnictví Evropské unie. Skládá se:', 'Z jednoho státního příslušníka každého členského státu.', 'Z důvěryhodných osob jmenovaných Evropským parlamentem.', 'Z osob jmenovaných Evropskou radou na základě konsenzu.', 'a', 'Otázka OK, odpověď OK\r'),
(278, 'Nestanoví-li Smlouva o Evropské unii a Smlouva o fungování Evropské unie jinak, rozhoduje Evropská rada:', 'Kvalifikovanou většinou.', 'Prostou většinou.', 'Konsenzem.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(279, 'Soudní dvůr Evropské unie a Účetní dvůr mají své sídlo v:', 'Haagu a Bruselu.', 'Lucemburku.', 'Štrasburku.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(280, 'Vnitrostátní parlamenty:', 'Se nepodílejí na činnosti Evropské unie.', 'Jsou informovány orgány Evropské unie a jsou jim postupovány návrhy legislativních aktů.', 'Spolurozhodují výlučně o otázkách, které se týkají hospodářské a měnové unie a měnové politiky členských států, jejichž měnou je euro.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(281, 'Blokační menšinu v Radě Evropské unie musí tvořit minimálně:', 'Čtyři členové Rady Evropské unie.', 'Pět členů Rady Evropské unie.', 'Šest členů Rady Evropské unie.', 'a', 'Otázka OK, odpověď OK\r'),
(282, 'Soudní dvůr Evropské unie:', 'Zajišťuje dodržování práva při výkladu a provádění smluv, na nichž je Evropská unie založena.', 'Zajišťuje uplatňování Smlouvy o Evropské unii a Smlouvy o fungování Evropské unie, jakož i opatření přijatých orgány Evropské unie na jejich základě.', 'Vykonává společně s Evropským parlamentem legislativní a rozpočtovou funkci.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(283, 'Listina základních práv Evropské unie:', 'Rozšiřuje pravomoci Evropské unie vymezené ve Smlouvě o Evropské unii a ve Smlouvě o fungování Evropské unie.', 'Nerozšiřuje pravomoci Evropské unie vymezené ve Smlouvě o Evropské unii a ve Smlouvě o fungování Evropské unie.', 'Je platná jen pro některé členské státy.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(284, 'Evropská unie nemá výlučnou pravomoc v oblasti:', 'Celní unie.', 'Společné obchodní politiky.', 'Školství.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(285, 'Mezi práva a povinnosti občanů Evropské unie stanovených smlouvami, na nichž je Evropská unie založena, nepatří:', 'Právo svobodně se pohybovat a pobývat na území členských států.', 'Právo volit hlavu státu v členském státě, ve kterém mají prokazatelně trvalý pobyt nejméně po dobu pěti let.', 'Právo volit a být volen ve volbách do Evropského parlamentu a v obecních volbách v členském státě, v němž mají bydliště, za stejných podmínek jako státní příslušníci tohoto státu.', 'b', 'Otázka OK, odpověď OK\r'),
(286, 'Evropská komise vykonává své funkce:', 'Zcela nezávisle.', 'V závislosti na pokynech členských států.', 'V závislosti na pokynech Evropského parlamentu.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(287, 'Každý občan Evropské unie:', 'Má petiční právo k Evropskému parlamentu.', 'Se může v krajních případech a prostřednictvím národního státu obracet na veřejného ochránce práv, a to v anglickém či francouzském jazyce.', 'Se může sám a bez omezení obracet na Soudní dvůr Evropské unie.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(288, 'Vnitřní trh zahrnuje prostor bez vnitřních hranic, v němž je zajištěn volný pohyb:', 'Zboží, osob, služeb a kapitálu.', 'Osob, investic, nerostných surovin a služeb.', 'Informací, osob, zboží a služeb.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(289, 'Smlouva o fungování Evropské unie zavádí pojem občanství Unie. To znamená, že:', 'Občan členského státu, pokud má zájem, může o toto občanství požádat.', 'Občanství Unie automaticky nahradilo občanství členských států.', 'Občanství Unie doplňuje občanství státu, nenahrazuje je.', 'c', 'Otázka OK, odpověď OK\r'),
(290, 'Zásada proporcionality znamená, že:', 'Obsah ani forma činnosti orgánů Evropské unie nesmí překročit rámec toho, co je nezbytné pro dosažení cílů smluv, na nichž je Evropská unie založena.', 'Orgány Evropské unie mohou jednat pouze v případě a za podmínek stanovených Smlouvou o Evropské unii a současně, pokud konkrétní pravomoc není svěřena členským státům.', 'Členské státy se navzájem respektují a pomáhají si při plnění úkolů vyplývajících ze smluv, na nichž je Evropská unie založena.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(291, 'Smlouva o založení Evropského společenství pro atomovou energii (EURATOM) podepsaná v roce 1957 nabyla platnosti:', 'Ve stejném roce jako byla podepsána.', 'V roce 1958.', 'Nenabyla nikdy platnosti.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(292, 'Které orgány Evropské unie přijímají řádným legislativním postupem společně na návrh Evropské komise nařízení, směrnice a rozhodnutí?', 'Rada a Evropská rada.', 'Evropský parlament a Rada.', 'Evropská rada a Účetní dvůr.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(293, 'Evropská unie má pravomoc provádět činnosti, jimiž podporuje, koordinuje nebo doplňuje činnosti členských států.  Mezi oblasti těchto činností nepatří:', 'Cestovní ruch.', 'Zachování biologických mořských zdrojů v rámci společné rybářské politiky.', 'Civilní ochrana.', 'b', 'Otázka OK, odpověď OK\r'),
(294, 'Legislativní akty přijaté orgány Evropské unie se vyhlašují:', 'V Úředním věstníku Evropské unie.', 'Ve Sbírce zákonů a mezinárodních smluv.', 'Ve Věstníku směrnic a nařízení Evropské unie.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(295, 'Výkon pravomocí Evropské unie se řídí zásadami:', 'Subsidiarity a občanské iniciativy.', 'Loajální spolupráce a humanismu.', 'Subsidiarity a proporcionality.', 'c', 'Otázka OK, odpověď OK (samo)\r'),
(296, 'Evropská unie je založena na Smlouvě o Evropské unii a Smlouvě o fungování Evropské unie. Tyto dvě smlouvy jsou vůči sobě v následujícím vztahu:', 'Obě smlouvy mají stejnou právní sílu.', 'Smlouva o Evropské unii je nadřazena Smlouvě o fungování Evropské unie.', 'Smlouva o fungování Evropské unie je nadřazena Smlouvě o Evropské unii.', 'a', 'Otázka OK, odpověď OK (samo)\r'),
(297, 'Dovozní nebo vývozní cla a poplatky s rovnocenným účinkem jsou mezi členskými státy Evropské unie:', 'Zakázány.', 'Povoleny.', 'Zakázány s výjimkou případů stanovených ve Smlouvě o Evropské unii a Smlouvě o fungování Evropské unie.', 'a', 'Otázka OK, odpověď OK\r'),
(298, 'Jedním z principů fungování Evropské unie je zásada subsidiarity. Subsidiarita znamená:', 'Právo institucí Evropské unie zasahovat do vnitřních věcí členských států Evropské unie.', 'Evropská unie je oprávněna jednat jen tehdy, nelze-li daného cíle dosáhnout efektivněji členskými státy na úrovni ústřední, regionální či místní.', 'Rozhodování v zájmu ústředních institucí bez ohledu na zájmy nižších úrovní.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(299, 'Funkční období Evropské komise je:', 'Čtyřleté.', 'Pětileté.', 'Šestileté.', 'b', 'Otázka OK, odpověď OK (samo)\r'),
(300, 'Evropská centrální banka má sídlo:', 'V Haagu.', 'V Bruselu.', 'Ve Frankfurtu nad Mohanem.', 'c', 'Otázka OK, odpověď OK (samo)\r');
